import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import {
  Avatar,
  Typography,
  Button,
  Hidden,
  IconButton,
  Snackbar,
  colors
} from '@material-ui/core';


const useStyles = makeStyles(theme => ({
  root: {},
  cover: {
    position: 'relative',
    height: 300,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    '&:before': {
      position: 'absolute',
      content: '" "',
      top: 0,
      left: 0,
      height: '100%',
      width: '100%',
      backgroundImage:
        'linear-gradient(-180deg, rgba(0,0,0,0.00) 58%, rgba(0,0,0,0.32) 100%)'
    },
    '&:hover': {
      '& $changeButton': {
        visibility: 'visible'
      }
    }
  },
  changeButton: {
    visibility: 'hidden',
    position: 'absolute',
    bottom: theme.spacing(3),
    right: theme.spacing(3),
    backgroundColor: colors.blueGrey[900],
    color: theme.palette.divider,
    [theme.breakpoints.down('md')]: {
      top: theme.spacing(3),
      bottom: 'auto'
    },
    '&:hover': {
      backgroundColor: colors.blueGrey[900]
    }
  },
  addPhotoIcon: {
    marginRight: theme.spacing(1)
  },
  container: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    padding: theme.spacing(2, 3),
    margin: '0 auto',
    position: 'relative',
    display: 'flex',
    flexWrap: 'wrap',
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column'
    }
  },
  avatar: {
    border: `2px solid ${theme.palette.divider}`,
    height: 120,
    width: 120,
    top: -60,
    left: theme.spacing(3),
    position: 'absolute'
  },
  details: {
    marginLeft: 136
  },
  actions: {
    marginLeft: 'auto',
    [theme.breakpoints.down('sm')]: {
      marginTop: theme.spacing(1)
    },
    '& > * + *': {
      marginLeft: theme.spacing(1)
    }
  },
  pendingButton: {
    color: theme.palette.divider,
    backgroundColor: colors.red[600],
    '&:hover': {
      backgroundColor: colors.red[900]
    }
  },
  personAddIcon: {
    marginRight: theme.spacing(1)
  },
  mailIcon: {
    marginRight: theme.spacing(1)
  }
}));

interface HeaderProps {
  className?:string,
  user: User
}

interface User {
  address: String
  avatar: String
  birthday: String
  email: String
  facebook: String
  first_name: String
  gender: String
  google_id: String
  id: String
  intro: String
  last_name: String
  phone: String
  mobile: String
  skype: String
  username: String
  viber: String
  website: String
  zalo: String
  created_at: String
  updated_at: String
}

const Header:React.FunctionComponent<HeaderProps> = (props:HeaderProps) => {
  const { className, ...rest } = props
  const classes = useStyles()
  const user = {
    name: 'Nguyễn Phi Hùng',
    bio: 'Chyên viên tư vấn',
    avatar: 'https://vcdn-giaitri.vnecdn.net/2020/01/23/C-OiQ-UUIAIycqR-1-1579796735_680x0.jpg',
    // cover: 'https://react-material-kit.devias.io/images/covers/cover_2.jpg',
    cover: "https://nharenhadep.com/wp-content/uploads/2018/06/slide-du-an-can-ho-tnr-kenton-node-50.jpg",
    connectedStatus: 'not_connected',
  }
  const [connectedStatus, setConnectedStatus] = useState(user.connectedStatus) // if rejected do not show the button
  // const [openSnackbar, setOpenSnackbar] = useState(false)
  // useEffect(() => {
  //   if (connectedStatus === 'pending') {
  //     setOpenSnackbar(true);
  //   }
  // }, [connectedStatus]);

  let _avatar = "https://lh4.googleusercontent.com/-lt8LRsMSuQI/AAAAAAAAAAI/AAAAAAAAAAA/AKF05nA_wP5leK8kO70YNbLvU5KYh7IpXg/photo.jpg"
  let __avatar = props.user.avatar

  console.log("KKKKK Header xxx", props.user)
  return (
    <div>
      <div className={classes.cover} style={{ backgroundImage: `url(${user.cover})`}}>
      </div>
      <div className={classes.container}>
        { avatar ? <Avatar alt="nhadatmoi.net" className={classes.avatar} src={__avatar} /> : "" }
        <div className={classes.details}>
          <Typography component="h2" gutterBottom variant="overline">
            Chuyên viên tư vấn
          </Typography>
          <Typography component="h1" variant="h4" >
            {props.user.first_name} {props.user.last_name}
          </Typography>
        </div>
        <Hidden smDown>
          <div className={classes.actions}>
            <a rel="nofollow" title="Facebook" target="_blank" href="https://www.facebook.com/profile.php?id=100022306084026"> 
              <img width="44px" height="44px" alt="Facebook" src="https://zozo.vn/public/theme/circle/images/icons/fb-mau.svg"/> 
            </a>
            <a title="Trò chuyện thông qua Facebook Messenger" target="_blank" href="http://m.me/925790224164852?ref=zozovn"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/fbmessenger.svg" alt="btn-facebook"/>
            </a>
            <a title="Chat với chúng tôi qua Zalo" target="_blank" href="https://zalo.me/0377535717"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/zalo.svg" alt="btn-zalo"/>
            </a>
            <a title="Gọi ngay" href="tel:0936162822"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/call.svg" alt="btn-callnow"/>
            </a>
          </div>
        </Hidden>
      </div>
    </div>
  )
}

export default Header;
