import React, { FunctionComponent } from "react";
import Members from "./Tabs/Members";
import Rating from "./Tabs/Rating";
import Introduce from "./Tabs/Introduce";
import AdSellLeases from "./Tabs/AdSellLeases";
import Header from "./Header";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Paper from "@material-ui/core/Paper";
import { ThemeProvider } from '@material-ui/core/styles';
import _ from 'lodash';
import { Tabs, Tab, Grid, Button, Typography, Avatar, Hidden, AppBar } from "@material-ui/core";
import { relatedChannel, indexStyles } from "./Constant";
import { createMuiTheme } from '@material-ui/core/styles';
import { GET_CHANNELS } from "../../graphql/channel/queries";
import { apolloClient } from "../../utils";
import LoadingCircle from "./LoadingCircle"

const themeInput = createMuiTheme({
  overrides: {
    MuiTab: {
      root: {
        padding: "6px",
      },
      wrapper: {
        fontSize: "13px",
        fontWeight: 700,
      },
    },
  },
});

interface IndexProps {
  userLogin: string
  channel: ChannelProp
  slug: string
};

interface Channels {
  edges: {
    node: {
      id: string
      name: string
      slug: string
      avatar: string 
      cover: string  
      official_status: number 
      follow_value: number
    }
  }
};

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
};


const tabs = [
  { value: "ad", label: "Tin rao" },
  { value: "introduce", label: "Giới thiệu" },
  { value: "members", label: "Môi giới" },
  { value: "rating", label: "Đánh giá" },
];

const Index: FunctionComponent<IndexProps> = (props: any) => {
  const classes = indexStyles();
  const [itemTab, setItemTab] = React.useState("rating");
  const [page, setPage] = React.useState<number>(0);
  const [channels, setChannels] = React.useState<Channels[]>([]);
  const [loading, setLoading] = React.useState(true);
  const channelId = _.get(props, "channel.id", "");

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value);
  };

  const changeTabs = (value: string) => {
    setItemTab(value);
  };

  React.useEffect(()=> {
    getChannels()
  })

  const getChannels = async () => { 
    let limit = 8 + 8*page
    let channels = await apolloClient.query({ query: GET_CHANNELS, variables: {
      "limit": limit,
      "page": 1,
      "filter": "{}",
    }})
    .then((response: any) => {
      let edges = _.get(response, "data.channels.edges", [])
      setChannels(edges)
      setLoading(false)
    });
  };

  const renderTabs = () => {
    return (
      <div>
        <Hidden smUp>
          {tabs.map((item: any, index: number) => {
            return (
              <div key={index} onClick={() => changeTabs(item.value)} className={ item.value == itemTab ? classes.tabItemChoose : classes.tabItem} >
                {item.label}
              </div>
            )
          })}
        </Hidden>
        <Paper square>
          <Hidden xsDown>
            <Tabs
              onChange={handleTabsChange}
              scrollButtons="auto"
              indicatorColor="primary"
              textColor="primary"
              variant="standard" 
              value={itemTab}
            >
              {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
            </Tabs>
          </Hidden>
        </Paper>
      </div>
    );
  };

  const renderTab = () => {
    return (
      <div>
        {itemTab === "ad" && <AdSellLeases channelId={channelId} />}
        {itemTab === "introduce" && <Introduce channelId={channelId} slug={props.slug}/>}
        {itemTab === "members" && <Members channelId={channelId}  />}
        {itemTab === "rating" && <Rating channelId={channelId} />}
      </div>
    );
  };
  
  const viewMore = (page) => {
    setPage(page)
    getChannels()
  }

  const renderRelatedChannel = () => {
    return (
      <div className={classes.relatedChannel}>
        <Typography variant="h6" className={classes.title}>Kênh liên quan</Typography>
        {loading ? <LoadingCircle /> : ""}
        {channels.map((element: any, index: number) => {
          return (
            <div className={classes.itemRelatedChannel} key={index} >
              <Grid container spacing={0} alignItems="flex-start" justify="flex-start">
                <Grid item xs={12}>
                  <div className={classes.avatarRelatedChannel}>
                    {element.node.avatar 
                    ? <Avatar src={element.node.avatar} className={classes.sizeAvatar} alt="" />
                    : <Avatar src={element.node.avatar} className={classes.sizeAvatar} alt="" >{_.get(element, "node.name[0]", "") }</Avatar>}
                  </div>
                  <div className={classes.contentFollow}>
                    <Typography className={classes.titleFollow}>
                      {element.node.name}
                    </Typography>
                    <Typography className={classes.numberFollow}>
                      {element.node.follow_value} Lượt theo dõi 
                    </Typography>
                    {!element.node.official_status ? <CheckCircleIcon className={classes.checkIcon}/> : ""}
                  </div>
                  <div className={classes.clearFloat}></div>
                </Grid>
              </Grid>
            </div>
          )
        })}
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Button onClick={() => viewMore(page + 1)} fullWidth className={classes.button}>
              Xem thêm
            </Button>
          </Grid>
        </Grid>
      </div>
    )
  }

  return (
    <div>
      <Header userLogin={props.userLogin} channel={props.channel} slug={props.slug}/>
      <div className={classes.paddingIndex}>
        {renderTabs()}
        <Grid container alignItems="flex-start" spacing={0} className={classes.paddingContentTab}>
          <Grid item xs={12} md={9}>
            {renderTab()}
          </Grid>
          <Grid item xs={12} md={3}>
            {renderRelatedChannel()}
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Index;
