import React, {FunctionComponent} from "react";
import ReplyIcon from '@material-ui/icons/Reply';
import PlaylistAddIcon from '@material-ui/icons/PlaylistAdd';
import ReportIcon from '@material-ui/icons/Report';
import Moment from 'react-moment';
import Button from "@material-ui/core/Button";
import dynamic from "next/dynamic";
import {type} from "os";
const SharePopup = dynamic(() => import('../Share/Share'), {
  ssr: false
});

interface ProjectMainProps {
  propertyOverview: {
    id: string,
    address: {
      text: string
    }
    title: string|null,
    updated_at: string|null,
    created_at: string|null,
    view_count: number
  },
  objectUrl: string,
  avatar: string,
  type: string,
  updateCompareData: (data: CompareDataProps) => void;
}

interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}

const TitleProject: FunctionComponent<ProjectMainProps> = (props: ProjectMainProps) =>
{
  const dataPrepare = {
    avatar: props.avatar,
    title: props.propertyOverview.title,
    id: props.propertyOverview.id,
    type: props.type
  };
  let clickAddToCompare = () => {
    props.updateCompareData(dataPrepare);
  };
  const [statusShare, setStatusShare] = React.useState<boolean>(false);
  return (
    <div className="title-project">
      <div className="title-project__address">
        <svg viewBox="0 0 384 512">
          <path d="M172.268 501.67C26.97 291.031 0 269.413 0 192 0 85.961 85.961 0 192 0s192 85.961 192 192c0 77.413-26.97 99.031-172.268 309.67-9.535 13.774-29.93 13.773-39.464 0zM192 272c44.183 0 80-35.817 80-80s-35.817-80-80-80-80 35.817-80 80 35.817 80 80 80z"/>
        </svg>
        {props.propertyOverview.address.text}</div>
      <h1 className="title-project__title">{props.propertyOverview.title}</h1>
      <div className="title-project__bottom">
        <div className="title-project__bottom__publish">{props.propertyOverview.view_count} lượt xem - Ngày đăng <Moment unix format="DD/MM/YYYY">
          {Number(props.propertyOverview.created_at)/1000}
        </Moment></div>
        <div className="title-project__bottom__share">
          <div className="title-project__bottom__share__item">
            <Button onClick={() => setStatusShare(true)} >
              <ReplyIcon/> Chia sẻ
            </Button>
          </div>
          <div className="title-project__bottom__share__item">
            <Button onClick={() => clickAddToCompare()}>
              <PlaylistAddIcon/> So sánh
            </Button>
          </div>
          <div className="title-project__bottom__share__item">
            <Button>
              <ReportIcon/> Báo cáo
            </Button>
          </div>
        </div>
      <SharePopup onClose={() => setStatusShare(false)} statusModal={statusShare} objectUrl={props.objectUrl}/>
      </div>
    </div>
  );
};

export default TitleProject;
