import React, { FunctionComponent } from "react";
import Agency from "./Tabs/Agency";
import Rating from "./Tabs/Rating";
import Introduce from "./Tabs/Introduce";
import AdSellLeases from "./Tabs/AdSellLeases";
import Header from "./Header";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Paper from "@material-ui/core/Paper";
import { ThemeProvider } from '@material-ui/core/styles';
import StyledCheckbox from "./StyledCheckbox";
import _ from 'lodash'
import {
  Tabs,
  Tab,
  Divider,
  Grid,
  Button,
  Typography,
  Avatar,
  Hidden,
} from "@material-ui/core";
import { relatedChannel, indexStyles } from "./Constant";
import { createMuiTheme } from '@material-ui/core/styles'
import { Badge } from '@material-ui/core'
import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles'

const themeInput = createMuiTheme({
  overrides: {
    MuiTab: {
      root: {
        padding: "6px",
      },
      wrapper: {
        fontSize: "13px",
        fontWeight: 700,
      },
    },
  },
});

interface IndexProps {
  comments: CommentsProp[];
  infoUser: UserProps;
  ratings: RatingsProp[];
}

interface CommentsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    like_number: string;
    created_at: string;
  };
}

interface UserProps {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  intro: string;
  id: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

interface RatingsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    rate: number;
    like_number: string;
    created_at: string;
  };
}

const Index: FunctionComponent<IndexProps> = (props: any) => {
  const classes = indexStyles();
  const [itemTab, setItemTab] = React.useState("introduce");
  const [count, setCount] = React.useState<number>(0);
  const tabs = [
    { value: "ad", label: "Tin rao" },
    { value: "introduce", label: "Giới thiệu" },
    { value: "agency", label: "Môi giới" },
    { value: "rating", label: "Đánh giá" },
  ];

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value);
  };
  return (
    <div>
      <Header />
      <div className={classes.paddingIndex}>
        {/* <Grid container spacing={0}>
          <Grid item xs={12} md={12}>
            <Paper square className={classes.rootTabs} style={{ padding: "0 4px" }}>
              <Hidden mdUp>
                <ThemeProvider theme={themeInput}>
                  <Tabs
                    onChange={handleTabsChange}
                    scrollButtons="auto"
                    indicatorColor="primary"
                    textColor="primary"
                    variant={"fullWidth"}
                    value={itemTab}
                  >
                    {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
                  </Tabs>
                </ThemeProvider>
              </Hidden>
              <Hidden smDown>
                <Tabs
                  onChange={handleTabsChange}
                  scrollButtons="auto"
                  indicatorColor="primary"
                  textColor="primary"
                  variant={ "standard" }
                  value={itemTab}
                >
                  {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
                </Tabs>
              </Hidden>
            </Paper>
          </Grid>
          <Grid item xs={12} md={9}>
            <div className="body_container">
              {itemTab === "ad" && <AdSellLeases userId={props.infoUser.id} />}
              {itemTab === "introduce" && <Introduce infoUser={props.infoUser} />}
              {itemTab === "agency" && <Agency infoUser={props.infoUser} />}
              {itemTab === "rating" && <Rating />}
            </div>
          </Grid>
          <Grid item xs={12} md={3}>
            <Typography variant="h6" className={classes.title}>
              Kênh liên quan
            </Typography>
            {relatedChannel.map((item: any, index: number) => {
              if (index < count * 6 + 6) {
                return (
                  <div className={classes.contentChannel} >
                    <Grid key={index} container spacing={4} alignItems="center" justify="center" className={classes.padingContent}>
                      <Grid item xs={12}>
                        <div className={classes.avatarLeft}>
                          <Avatar src={item.image} className={classes.large} alt="" />
                        </div>
                        <div className={classes.contentFollow} >
                          <Typography className={classes.titleFollow}>
                            {item.title}
                          </Typography>
                          <Typography className={classes.follow}>
                            {item.numberfollow}Lượt theo dõi 
                          </Typography>
                          {item.follow && <CheckCircleIcon style={{ color: "#045EA8", fontSize: "15px", float: "right" }} />}
                        </div>
                        <div className={classes.clearFloat}></div>
                      </Grid>
                    </Grid>
                  </div>
                );
              } else {
                return "";
              }
            })}
            <Grid container spacing={4}>
              <Grid item xs={12}>
                <Button onClick={() => setCount(count + 1)} fullWidth className={classes.button}>
                  Xem thêm
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid> */}
      </div>
    </div>
  );
};

export default Index;
