import React, { Component, Fragment } from 'react';
import Popper, { PopperPlacementType } from '@material-ui/core/Popper';
import SpeedDialIcon from '@material-ui/lab/SpeedDialIcon';
import SpeedDial, { SpeedDialProps } from '@material-ui/lab/SpeedDial';
import SpeedDialAction from '@material-ui/lab/SpeedDialAction';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import WarningIcon from '@material-ui/icons/Warning';
import {
  Grid,
  Button,
  InputBase,
  Fade,
  Typography,
  Paper,
  Card, 
  CardHeader,
  CardContent,
  CardActionArea, 
  CardMedia,
  Avatar,
  TextField,
  Badge,
  Divider,
  Tooltip,
  Input,
  Chip,
  IconButton

} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import TagFacesIcon from '@material-ui/icons/TagFaces';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import DirectionsIcon from '@material-ui/icons/Directions';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import CloseIcon from '@material-ui/icons/Close';
import SendIcon from '@material-ui/icons/Send';
import axios, {Method} from "axios";
import { dataEmoji, arrChat, giphyDefault } from "./constant"

// import 'emoji-mart/css/emoji-mart.css'
// import { Picker } from 'emoji-mart'


import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles';
// import Item from 'antd/lib/list/Item';

const theme = createMuiTheme({
  overrides: {
    MuiInput: {
      root: {
        position: "initial",
      },
      fullWidth: {
        minHeight: "50px"
      }
    }
  },
});

const themeTextfieldAdmin = createMuiTheme({
  overrides: {
    MuiTextField: {
      root: {
        borderRadius: "16px!important",
      },
    },
    MuiOutlinedInput: {
      multiline: {
        borderRadius: "16px!important",
      },
      inputMultiline: {
        fontSize: "13px"
      }
    }
  }
})

const themeTextfieldUser = createMuiTheme({
  overrides: {
    MuiInput: {
      root: {
        borderRadius: "16px!important",
      },
    },
    MuiOutlinedInput: {
      multiline: {
        borderRadius: "16px!important",
      },
      inputMultiline: {
        fontSize: "13px"
      }
    }
  }
})

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: 550,
      zIndex: 900,
    },
    textAdmin: {
      // borderRadius: "8px!important",
      backgroundColor: "#DBF4FD",
      color: "white",
      // fontSize: "13px",
      border: "1px "
    },
    textUser: {
      backgroundColor: "white",
      color: "black",
      // fontSize: "13px",
    },
    textFieldChatContent: {
      marginLeft: "30px"
    },
   
    dispalyImageGiphy: {
      width: "75px", 
      height: "60px", 
      display: 'inline-block', 
      padding: '2px'
    },
    imgListGiphy: {
      width:"100%", 
      height: "100%", 
      borderRadius: '10px',
    },
    viewContentImageGiphy: {
      // textAlign: "right",
      float: "right",
      width: "250px", 
      height: "150px", 
      padding: "5px 0px 5px 0px",
    },
    imgGiphy: {
      width:"100%", 
      height: "100%", 
    },
    textFieldUserChatContent: {
      marginLeft: "30px", 
      padding: '5px', 
      borderRadius: "16px!important"
    },
    errInternet: {
      // position: "absolute",
      // bottom: '60px',
      padding: "20px",
      color: "red",
      fontSize: '13px',
    },
    texting: {
      fontSize: '13px',
      color: '#aaa',
      padding: "5px",
      textAlign: 'right'
    },
    iconWaring: {
      fontSize:"20px",
    },
    rootPaper: {
      display: 'flex',
      textAlign: 'center',
      margin: "5px 5px 5px 5px",
      backgroundColor: "#f0f0f0",
      alignItems: 'center',
      height: "27px",
      width: "92%",
    },
    inputBaseGiphy: {
      marginLeft: theme.spacing(1),
      flex: 1,
    },
    buttonEmojiGiphy: {
      border: '1px solid white', 
      backgroundColor: "#045EA8",
      color: 'white', 
      borderRadius: "10px", 
      padding: "5px",
      marginLeft: "5px"
    },
    iconButton: {
      padding: 3,
    },
    inputBase: {
      padding: '2px 4px',
      display: 'flex',
      alignItems: 'center',
      width: "100%",
      position: "absolute",
      boxShadow: "0px -1px 3px black",
      // borderTop: "3px solid gray",
      borderRadius: "0px 0px",
      bottom: "0px"
    },
    input: {
      marginLeft: theme.spacing(1),
      height: 50 ,
      flex: 1,
    },
    divider: {
      height: 28,
      margin: 4,
    },
    typography: {
      padding: theme.spacing(2),
    },
    positionSizeViewChat: {
      backgroundColor: "white",
      zIndex: 900,
      height:"550px", 
      width: "350px",
      position: "fixed",
      right: '30px',
      bottom: "70px",
      boxShadow: "0px -1px 3px #b3c5cc",
    },
    emojiGiphy: {
      height:"200px", 
      width: "320px",
      textAlign: 'center',
      // backgroundColor: "#bff2ed",
      borderTop: '1px solid gray',
    },
    textField: {
      position: "absolute",
      bottom: "0px"
    },
    exampleWrapper: {
      position: 'fixed',
      right: "60px", 
      bottom: "2px",
    },
    large: {
      width: theme.spacing(7),
      height: theme.spacing(7),
    },
    avatarAdminText: {
      width: theme.spacing(3),
      height: theme.spacing(3),
    },
    sizeAvatarSmall: {
      width: theme.spacing(5),
      height: theme.spacing(5),
    },
    positionAvatar: {
      position: 'fixed',
      right: "15px", 
      bottom: "80px",
    },
    viewAvatarChatContent: {
      float: "left",
      marginTop: "10px"
    },
    radioGroup: {
      margin: theme.spacing(1, 0),
    },
    avatar: {
      backgroundColor: "blue",
    },
    media: {
      height: 100,
      backgroundColor: "blue"
    },
    hearder: {
      backgroundColor: "#1a75ff",
      color: "white"
    },
    speedDial: {
      position: 'absolute',
      '&.MuiSpeedDial-directionUp, &.MuiSpeedDial-directionLeft': {
        bottom: theme.spacing(2),
        right: theme.spacing(2),
      },
      '&.MuiSpeedDial-directionDown, &.MuiSpeedDial-directionRight': {
        top: theme.spacing(2),
        left: theme.spacing(2),
      },
    },
    sizeIcon: {
      fontSize: '30px'
    },
    textSizeNewDate: {
      fontSize: "13px"
    }
  }),
);

const StyledBadge = withStyles((theme: Theme) =>
  createStyles({
    badge: {
      backgroundColor: '#44b700',
      color: '#44b700',
      boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
      '&::after': {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        borderRadius: '50%',
        animation: '$ripple 1.2s infinite ease-in-out',
        border: '1px solid currentColor',
        content: '""',
      },
    },
    '@keyframes ripple': {
      '0%': {
        transform: 'scale(.8)',
        opacity: 1,
      },
      '100%': {
        transform: 'scale(2.4)',
        opacity: 0,
      },
    },
  }),
)(Badge);
 

let valueInputChat = ""

export default function PositionedPopper() {
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const [online, setOnline] = React.useState<boolean>(true)
  const [internet, setInternet] = React.useState<boolean>(false)
  const classes = useStyles();
  const [texting, setTexting] = React.useState<boolean>(false)
  const [valueInput, setValueInput] = React.useState("")
  const [giphyData, setGiphyData] = React.useState<any>()
  const [openEmojiGiphy, setOpenEmojiGiphy] = React.useState<boolean>(false)
  const [indexEmoji, setIndexEmoji] = React.useState<number>()
  const [valueSearchGiphy, setValueSearchGiphy] = React.useState("")

  const onCloseChat = () => {
    setOpen(false)
  }

  const setOnKeyPress = (event: any) => {
    setTexting(true)
  }
  
  const setOnBlur = (event: any) => {
    setTexting(true)
  }

  const setOnKeyDown = (event: any) => {
    if(event.keyCode == 13){
      arrChat.push({ message: event.target.value, type: 'user', image: { url: "", width: "", height: "" }, date: String(new Date().getTime()) })
      setValueInput("")
    }
  }

  const senMesssage = () => {
    arrChat.push({ message: valueInput, type: 'user', image: { url: "", width: "", height: "" }, date: String(new Date().getTime()) })
    setValueInput("")
  }

  const renderHeader = () => {
    return (
      <CardHeader
        className={classes.hearder}
        avatar={
          <StyledBadge
            overlap="circle"
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            variant= { online ? "dot" : "standard" }
          >
            <Avatar 
              className={classes.sizeAvatarSmall} 
              alt="nhadatmoi.net" 
              src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
            />
          </StyledBadge>
        }
        title="Nguyễn Thanh Huyền"
        action={
          <IconButton color='inherit' aria-label="settings" onClick={() => onCloseChat()} >
            <CloseIcon/>
          </IconButton>
        }
      />
    )
  }
  
  const setChangeInput = (value: any) => {
    setValueInput(value)
  }

    
  let api = "https://api.giphy.com/v1/gifs/search?"
  let apiKey = "api_key=UgTMyvovmg74m8fPN6qYiB623nwS1IPc"
  let limit = "&limit=20"
  let offset = "&offset=0"
  let rating = "&rating=G"
  let lang = "&lang=vi"
  const searchGiphy = async (event: any)=>  { 
    setValueSearchGiphy(event.target.value)
    let query =  `&q=${event.target.value}`
    let url = api + apiKey + query + limit + offset + rating + lang
    let getData = await axios.get(url, { params: { ID: 12345 } })
      .then(function (response) {
        // setGiphyData(response.data.data)
      })
  }

  // let dataSearch: any = []
  let dataSearch = giphyDefault
  // if(Array.isArray(giphyData)){
  //   giphyData.map((item: any, index: number) => {
  //     // dataSearch.push({ image:  item.images.original.url })
  //     dataSearch.push({ 
  //       image: {
  //         url: item.images.fixed_width.url,
  //         height: item.images.fixed_width.height,
  //         width: item.images.fixed_width.width,
  //       }})
  //   })
  // }

  const setItemGiphy = (index: number, name: string) => {
    setOpenEmojiGiphy(!openEmojiGiphy)
    if(name == "giphyDefault") {
      arrChat.push({ 
        message: "", 
        type: 'user', 
        image: {
          url: giphyDefault[index].image.url,
          width: giphyDefault[index].image.width,
          height: giphyDefault[index].image.height,
        },
        date: String(new Date().getTime()) 
      })
    } else if ( name == "dataSearch") {
      arrChat.push({ 
        message: "", 
        type: 'user', 
        image: {
          url: giphyDefault[index].image.url,
          width: giphyDefault[index].image.width,
          height: giphyDefault[index].image.height,
        },
        date: String(new Date().getTime())
      })
    }
  }
  const renderEmojiGiphy = () => {
    return (
      <div className={classes.emojiGiphy}>
          <button className={classes.buttonEmojiGiphy} >
            Biểu tượng
          </button>
          <button className={classes.buttonEmojiGiphy} style={{ backgroundColor: "#85adad"}}>
            Ảnh động
          </button>
        <Paper component="form" className={classes.rootPaper}>
          <IconButton type="submit" className={classes.iconButton} aria-label="search">
            <SearchIcon />
          </IconButton>
          <InputBase
            onChange={(e) => searchGiphy(e)}
            className={classes.inputBaseGiphy}
            placeholder="Tìm kiếm"
          />
          {/* <IconButton type="submit" className={classes.iconButton} aria-label="search">
            <SearchIcon />
          </IconButton> */}
        </Paper>
        <div style={{ overflowX: 'auto', height: '130px' }}>
          {dataEmoji.map((item: any, index: number) => {
            return (
              <div onClick={() => setIndexEmoji(index)} key={index} style={{ display: 'inline-block', fontSize: '20px', cursor: "pointer"}}>
                {item.native}
              </div>
            )
          })}
        </div>
        {/* <div style={{ overflowX: 'auto', height: '130px' }}>
          {valueSearchGiphy 
            ? dataSearch.map((item: any, index: number) => {
                return (
                  <div className={classes.dispalyImageGiphy} >
                    <img 
                      className={classes.imgListGiphy} 
                      alt="giphy" 
                      src={item.image.url} 
                      onClick={() => setItemGiphy(index, "dataSearch")} 
                    />
                  </div>
                )
              })
            : giphyDefault.map((item: any, index: number) => {
                return (
                  <div className={classes.dispalyImageGiphy} >
                    <img 
                      className={classes.imgListGiphy} 
                      alt="giphy" 
                      src={item.image.url} 
                      onClick={() => setItemGiphy(index, "giphyDefault")} 
                    />
                  </div>
                )
              })
          }
        </div> */}
      
      </div>
    )
  }

  const renderContentChat = () => {
    return (
      <div style={{ overflowY: "auto", padding: "8px", height: "430px" }}>
        <Typography align="center" className={classes.textSizeNewDate}>
          {moment().format('LLL')}
        </Typography>
        <Divider />
        {arrChat.map((item: any, index: number) => {
          let checkNewDate = false
          if(index > 0 && (moment(Number(arrChat[index - 1].date)).format('YYYY-MM-DD') != moment(Number(arrChat[index].date)).format('YYYY-MM-DD'))) {
            checkNewDate = true
          }
          let setViewAvatar = false
          if(item.type === 'admin' && index > 0 && (index === arrChat.length || arrChat[index - 1].type != arrChat[index].type)) {
            setViewAvatar =  true
          }
          return (
            <div key={index}>
              { checkNewDate && <Typography align="center" className={classes.textSizeNewDate}>
                {moment(Number(item.date)).format("LLL")} <Divider /></Typography>}
              { item.type === 'admin' 
              ? <ThemeProvider theme={themeTextfieldAdmin}>
                  <div className={classes.viewAvatarChatContent}>
                    {
                      setViewAvatar 
                      ? <Avatar 
                          className={classes.avatarAdminText} 
                          alt="nhadatmoi.net" 
                          src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
                        />
                      : ""
                    }
                  </div>
                  <div className={classes.textFieldChatContent}>
                    {
                      item.image.url 
                      ? <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                          <div className={classes.viewContentImageGiphy} style={{ width: `${item.image.width}px`, height: `${item.image.height}px`}} >
                            <img 
                              className={classes.imgGiphy} 
                              alt="giphy" 
                              src={item.image.url} 
                            />
                          </div>
                        </Tooltip>
                      : <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                          <TextField 
                            fullWidth
                            label=""
                            className={classes.textAdmin}
                            value={item.message}
                            margin="dense"
                            id="outlined-basic" 
                            multiline={true}
                            InputProps={{
                              readOnly: true
                            }}
                            variant="outlined" 
                          />
                        </Tooltip>
                    }
                  </div>
                  </ThemeProvider>
              : <ThemeProvider theme={themeTextfieldUser}>
                  {
                    item.image.url
                    ? <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                        <div className={classes.viewContentImageGiphy} style={{ width: `${item.image.width}px`, height: `${item.image.height}px`}} >
                          <img 
                            className={classes.imgGiphy} 
                            alt="giphy" 
                            src={item.image.url} 
                          />
                        </div>
                      </Tooltip>
                    : <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                        <div className={classes.textFieldUserChatContent}>
                          {/* <TextField 
                            fullWidth
                            label=""
                            className={classes.textUser}
                            value={item.message}
                            margin="dense"
                            id="outlined-basic" 
                            multiline={true}
                            InputProps={{
                              readOnly: true
                            }}
                            variant="outlined" 
                          />  */}
                          <span style={{fontSize : '100px'}}>&#9199;</span>
                          <p>I will display &#9199;</p>
                          <p>I will display &#x23EF;</p>
                          <p>{item.message}</p>
                        </div>
                      </Tooltip>
                  }
                  </ThemeProvider>
              }
            </div>
          )
        })}
        <div>
          {openEmojiGiphy && renderEmojiGiphy()}
        </div>
        {/* {!internet 
          ? <p className={classes.errInternet}>
              <u>Không thể gửi tin nhắn này. Kiểm tra kết nối internet và thử lại</u>
            </p>
          : ""
        }
        {texting && <p className={classes.texting}>Đang nhập ...</p>} */}
      </div>
    )
  }
 
  console.log("valueInputChat", valueInputChat) 
  valueInputChat = valueInputChat + "🎅🏼" + valueInput
  const renderInput = () => {
    return (
      <Paper component="form" className={classes.inputBase}>
        <ThemeProvider theme={theme}>
          <Input 
            fullWidth
            // disabled={internet}
            margin="dense"
            id="outlined-basic" 
            autoFocus={true}
            // value={valueInput}
            value={"🎅🏼🎅🏼🎅🏼🎅🏼🎅🏼🎅🏼" + "text text text &#129409;"}
            multiline={true}
            onChange={(e) => setChangeInput(e.target.value)}
            onKeyPress={(e) => setOnKeyPress(e)}
            onBlur={(e) => setOnBlur(e)}
            onKeyDown={(e) => setOnKeyDown(e)}
            placeholder="Nhập tin nhắn ..."
            autoComplete="false"
          />
          { openEmojiGiphy 
            ? <Tooltip title="Chèn biểu tượng" placement="top"> 
                <ExpandMoreIcon 
                  className={classes.sizeIcon}
                  onClick={() => setOpenEmojiGiphy(!openEmojiGiphy)}
                  color="primary"
                />
              </Tooltip> 
            : <Tooltip title="Chèn biểu tượng" placement="top"> 
                <TagFacesIcon 
                  className={classes.sizeIcon}
                  onClick={() => setOpenEmojiGiphy(!openEmojiGiphy)}
                  color="primary"
                />
              </Tooltip>
          }
          { valueInput
            ? <Tooltip title="Thêm tập tin" placement="top">
                <SendIcon 
                  onClick={senMesssage} 
                  className={classes.sizeIcon} color="primary"
                />
              </Tooltip>
            : <Tooltip title="Thêm tập tin" placement="top">
                <AddCircleOutlineIcon className={classes.sizeIcon} color="primary"/>
              </Tooltip>
          }
        </ThemeProvider>
      </Paper>
    )
  }

  const viewChat = () => {
    return (
      <Popper 
        open={open} 
        anchorEl={anchorEl}
        placement={placement} 
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.positionSizeViewChat} >
                {renderHeader()}
                {renderContentChat()}
                {renderInput()}
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  }

  const handleClick = (newPlacement: PopperPlacementType) => {
    setOpen(true)
    setPlacement(newPlacement);
  }

  const renderAvatar = () => {
    return (
      <div className={classes.positionAvatar}>
        <StyledBadge
          overlap="circle"
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'right',
          }}
          // variant= { online ? "dot" : "standard" }
        >
          <Avatar 
            onClick={() => handleClick("top-start")}
            className={classes.large} 
            alt="nhadatmoi.net" 
            src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
            // src="https://file.vforum.vn/hinh/2018/03/hinh-mau-trang-tinh-tron-hinh-nen-anh-dai-dien-anh-bia-toan-mau-trang1.png"
          />
        </StyledBadge>
      </div>
    )
  }

  return (
    <div className={classes.root}>
      {viewChat()}
      <Grid container justify="center">
        <Grid item>
          { !open && renderAvatar()}
        </Grid>
      </Grid>
    </div>
  );
}
