import React from "react";
import clsx from "clsx";
import { Grid, Typography } from "@material-ui/core";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import { List, Avatar } from "@material-ui/core";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import WcIcon from "@material-ui/icons/Wc";
import PhoneIcon from "@material-ui/icons/Phone";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import EmailIcon from "@material-ui/icons/Email";
import { agency } from "../Constant";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    color: "#aaa",
    backgroundColor: theme.palette.background.paper,
  },
  avatar: {
    border: `2px solid ${theme.palette.divider}`,
    height: 120,
    width: 120,
    top: -60,
    left: theme.spacing(3),
    position: "absolute",
    [theme.breakpoints.down("sm")]: {
      height: 80,
      width: 80,
    },
  },
  posts: {
    marginTop: theme.spacing(2),
  },
  post: {
    marginBottom: theme.spacing(2),
  },
}));

interface InfoProps {
  className?: string;
  infoUser: User;
}

interface User {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const { className } = props;
  const classes = useStyles();
  let userName = `${props.infoUser.first_name} ${props.infoUser.last_name}`;
  return (
    <div className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        {agency.map((item: any, index: number) => {
          return (
            <Grid key={index} item xs={2}>
              <div style={{ border: "2px solid blue", borderRadius: "4px", height: "200px", width: "100%" }}>
                <Avatar
                  alt="nhadatmoi.net"
                  className={classes.avatar}
                  src={"https://news.mogi.vn/wp-content/uploads/2018/08/anh-nha-dep-13.jpg"}
                />
              </div>
            </Grid>
          );
        })}
      </Grid>
    </div>
  );
};

export default Info;
