import React from 'react';
import { Typography, CardHeader, Avatar, Grid } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import { ratings, ratingStyles } from "../Constant"
import { apolloClient } from "../../../utils";
import { GET_RATINGS_CHANNEL_BY_ID } from "../../../graphql/channel/queries";
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

interface Props {
  channelId: string
}

interface RatingProps {
  node: {
    id: string
    user_id: {
      id: string
      first_name: string
      last_name: string
      avatar: string
    }
    ad_sell_lease_id: {
      id: string
      title: string
    }
    rate: Number
    created_at: string
  }
}

const avatar = "https://1.bp.blogspot.com/-gx54Ig64I_k/WnktM_nzPXI/AAAAAAAAHgw/E7pSRwrXu_c3tSSg-vbO2iQgg4GbVxGKwCLcBGAs/s1600/anh-girl-xinh-9x-kute%2B%252811%2529.jpg"
const Ratings: React.FunctionComponent<Props> = (props: Props) => {
  const classes = ratingStyles();
  const [ratingsChannel, setRatingsChannel] = React.useState<RatingProps[]>([])
  const getAdChannel = async ()=> { 
    let id = props.channelId
    let ratingsChannel = await apolloClient.query({ query: GET_RATINGS_CHANNEL_BY_ID, variables: {
      "id": id,
      "limit": 18,
      "page": 1,
    }})
    .then((response: any) => {
      return response.data.channelAdSellLeaseRatings.edges
    })
    if(Array.isArray(ratingsChannel)){
      setRatingsChannel(ratingsChannel)
    }
  };

  React.useEffect(() => {
    getAdChannel()
  })

  const renderRating = () => {
    return ratings.map((item: any, index: number) => {
      return (
        <div key={index}>
          <CardHeader 
            avatar={ <Avatar alt="Reviewer" src={avatar}>{item.name}</Avatar>}
            disableTypography
            subheader={
              <div className={classes.subheader}>
                <Typography variant="body2" className={classes.rating}>Đánh giá bởi:</Typography>
                <Typography className={classes.userRating}>{item.name}</Typography><br />
                <Rating
                  disabled
                  name="simple-controlled"
                  value={item.rating}
                  style={{ color: "#045EA8" }}
                />
                <Typography variant="body2">{moment(item.time).fromNow()}</Typography>
              </div>
            }
          />
        </div>
      )  
    }) 
  }

  const renderRatingChanel = () => {
    return ratingsChannel.map((item: any, index: number) => {
      return (
        <div key={index}>
          <CardHeader 
            avatar={ <Avatar alt="Reviewer" src={item.node.user_id.avatar || avatar}>{item.node.user_id.avatar}</Avatar>}
            disableTypography
            subheader={
              <div className={classes.subheader}>
                <Typography variant="body2" className={classes.rating}>Đánh giá bởi:</Typography>
                <Typography className={classes.userRating}>{item.node.user_id.first_name} {item.node.user_id.last_name}</Typography><br />
                <Rating
                  disabled
                  name="simple-controlled"
                  value={item.node.rate}
                  style={{ color: "#045EA8" }}
                />
                <Typography variant="body2">{moment(Number(item.node.created_at)).fromNow()}</Typography>
              </div>
            }
          />
        </div>
      )  
    }) 
  }

  const renderAverageRating = () => {
    const total = ratingsChannel.length
    return (
      <div className={classes.averageRating}>
        <div className={classes.averageRatingTotal} >
          <p className={classes.averageRatingLerver}>5.0</p>
          <Grid container alignItems="center" justify="center" spacing={4}>
            <Grid item xs={8}>
              <Rating disabled name="simple-controlled" value={5} className={classes.averageRatingColor}/>
            </Grid>
            <Grid item xs={4}>{total}</Grid>
          </Grid>
        </div>
        <div className={classes.averageRatingDescribe}>
            <div className={classes.averageRatingDescribeFive}>
              <div className={classes.averageRatingDescribeOne} style={{ width: '60%', height: 10, backgroundColor: "#045EA8", borderRadius: 5, position: "absolute"}} ></div>
            </div>
            <div style={{ width: '100%', height: 10, backgroundColor: "#aaa", borderRadius: 5, margin: '20px', position: "relative" }} >
              <div style={{ width: '10%', height: 10, backgroundColor: "#045EA8", borderRadius: 5, position: "absolute"}} ></div>
            </div>
            <div style={{ width: '100%', height: 10, backgroundColor: "#aaa", borderRadius: 5, margin: '20px', position: "relative" }} >
              <div style={{ width: '5%', height: 10, backgroundColor: "#045EA8", borderRadius: 5, position: "absolute"}} ></div>
            </div>
            <div style={{ width: '100%', height: 10, backgroundColor: "#aaa", borderRadius: 5, margin: '20px', position: "relative" }} >
              <div style={{ width: '5%', height: 10, backgroundColor: "#045EA8", borderRadius: 5, position: "absolute"}} ></div>
            </div>
            <div style={{ width: '100%', height: 10, backgroundColor: "#aaa", borderRadius: 5, margin: '20px', position: "relative" }} >
              <div style={{ width: '0%', height: 10, backgroundColor: "#045EA8", borderRadius: 5, position: "absolute"}} ></div>
            </div>
        </div>
        <div style={{ clear: "both" }}></div>
      </div>
    )
  }

  return (
    <div className={classes.rootRating}>
      <Typography variant="h5" className={classes.titleRating}>Đánh giá chất lượng</Typography>
      {renderAverageRating()}
      <div style={{ paddingTop: 20 }}>
        <Typography variant="h5" className={classes.titleRating}>Đánh giá gần nhất</Typography>
        {Array.isArray(ratings) ? renderRatingChanel() : <Typography align="center">Không có đánh giá nào</Typography> }
        {Array.isArray(ratings) ? renderRating() : <Typography align="center">Không có đánh giá nào</Typography> }
      </div>
    </div>
  );
};


export default Ratings;
