import gql from "graphql-tag";

const CREATE_MESSAGE = gql`
  mutation conversation($input: ConversationInput!) {
    conversation(input: $input) {
      id
      chat {
        send_user_id
        message {
          text
        }
      }
    }
  }
`;

const FOLLOW_CHANNEL = gql`
  mutation channelFollow ($id: String!) {
    channelFollow (id: $id) {
      id
      follow_value
    }
  }
`;

const UNFOLLOW_CHANNEL = gql`
  mutation channelFollow ($id: String!) {
    channelFollow (id: $id) {
      id
      follow_value
    }
  }
`;




export {
  FOLLOW_CHANNEL,
  UNFOLLOW_CHANNEL
}
