      image: "https://media.ngoisao.vn/news/2018/08/14/phong-thuy10-ngoisao.vn-w580-h387.jpg",
    },
    {
      title: "Thuê văn phòng giá rẻ",
      follow: true,
      numberfollow: "28.365",
      image: "https://cafefcdn.com/thumb_w/650/2017/du-an-vincity-tay-mo-dai-mo-1489197061940.jpg",
    },
    {
      title: "Cho thuê bất động sản",
      follow: true,
      numberfollow: "108.365",
      image: "https://phongthuytamnguyen.com/uploads/images/2018_06_16_fea1a1c344.jpeg",
    },
  ];

  return (
    <Page title="Profile">
      <Header infoUser={props.infoUser} />
      <CardContent>
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Paper square className={classes.rootTabs}>
              <Tabs
                onChange={handleTabsChange}
                scrollButtons="auto"
                indicatorColor="primary"
                textColor="primary"
                value={itemTab}
              >
                {tabs.map((tab) => (
                  <Tab key={tab.value} label={tab.label} value={tab.value} />
                ))}
              </Tabs>
            </Paper>
            <Divider className={classes.divider} />
          </Grid>
          <Grid item xs={9}>
            <div>
              {itemTab === "home" && <Home infoUser={props.infoUser} />}
              {itemTab === "work" && <Work infoUser={props.infoUser} />}
              {itemTab === "video" && <Video infoUser={props.infoUser} />}
              {itemTab === "introduce" && <Introduce infoUser={props.infoUser} />}
              {itemTab === "info" && <Info infoUser={props.infoUser} />}
              {itemTab === "ad" && <AdSellLeases userId={props.infoUser.id} />}
            </div>
          </Grid>
          <Grid item xs={3}>
            <Typography variant="h6" style={{ padding: "0px 0px 8px 0px" }}>
              Kênh liên quan
            </Typography>
            {relatedChannel.map((item: any, index: number) => {
              if (index < count * 6 + 6) {
                return (
                  <div
                    style={{
                      width: "100%",
                      height: "64px",
                      border: "2px solid #D9EDFE",
                      borderRadius: "4px",
                      margin: "0px 0px 8px 0px",
                      // padding: "8px"
                    }}
                  >
                    <Grid
                      key={index}
                      container
                      alignItems="center"
                      justify="center"
                      spacing={4}
                      style={{ padding: "4px" }}
                    >
                      <Grid item xs={12}>
                        <div style={{ float: "left" }}>
                          <Avatar src={item.image} className={classes.large} alt="" />
                        </div>
                        <div style={{ margin: "0px 0px 0px 56px" }}>
                          <Typography style={{ display: "inline", textOverflow: "ellipsis", width: "80%" }}>
                            {item.title}
                          </Typography>
                          {item.follow && <StyledCheckbox defaultChecked={item.follow} />}
                          <Typography style={{ color: "#aaa", fontSize: "13px" }}>
                            {item.numberfollow} Lượt theo dõi
                          </Typography>
                        </div>
                        <div style={{ clear: "both" }}></div>
                      </Grid>
                    </Grid>
                  </div>
                );
              } else {
                return "";
              }
            })}
            <Grid container spacing={4}>
              <Grid item xs={12}>
                <Button
                  fullWidth
                  style={{
                    border: "2px solid #D9EDFE",
                    borderRadius: "4px",
                    margin: "0px 0px 8px 0px",
                  }}
                  onClick={() => setCount(count + 1)}
                >
                  Xem thêm
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </CardContent>
    </Page>
  );
};

export default Index;
