import React, { useEffect } from "react";
import {NextPage} from 'next';
import Layout from '../../components/Layout/Layout';
import {mainURL} from "../../constants";
import "../../assets/scss/style.scss";
import Users from "../../components/Users/Index"

import {} from 'react';
import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/Project/ProjectMain";
import DetailInvestor from "../../components/Investor/DetailInvestor"
import {apolloClient} from "../../utils";
import {GET_PROJECT_BY_SLUG} from "../../graphql/project/queries";
import {GET_INVESTOR_BY_ID} from "../../graphql/investor/queries"
import StringProcess from "../../modules/stringProcess";
import {localStorageCompareKey} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import {COUNT_VIEW_PROJECT} from "../../graphql/project/mutation";


interface ProjectItemProp {
  id: string,
  investor: string,
  price: string,
  name: string,
  land_area: string,
  image: ImageProp[],
  video: string,
  financial_support: string,
  position_content: string,
  infrastructure_content: string,
  seo_title: string|null,
  seo_description: string|null,
  address: { 
    text: string,
    ward: string, 
    district: string, 
    city: string 
  },
  block: {
    name: string,
    image: ImageProp,
  }[],
  cover_image: string,
  intro_content: string
  created_at: string
  updated_at: string
  view_count: number
  google_map_latitude: string
  google_map_longitude: string
}
interface ImageProp { 
  alt_text: string 
  description: string
  title: string
  src: string
}
interface UserProps {
  url: { query: { slug: string }},
  projectDetail: { data: { project: ProjectItemProp } },
  slug: string
}
interface QueryProp { 
  query: { slug: string } 
}
interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}
interface Investor {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
  address: {
   text: string
  }
  phone: string
  mobile: string
}
interface ImageProp {
  alt_text: string
  description: string
  title: string
  src: string
}
 


const UserPage: NextPage<UserProps> = (props: UserProps) => {
  // let pageTitle = 'Giới thiệu về Nhà Đất Mới - Website thương mại điện tử Bất động sản';
  let pageTitle = "Microsoft - Website"
  let pageDescription = 'Tin rao của bạn sẽ được tiếp cận một lượng khách hàng lớn nhất bằng công nghệ. Đăng tin theo cách của bạn, còn lại để Nhà Đất Mới lo. Mang tới cho người mua và người bán những giao dịch kiểu mới: nhanh, chuẩn, chính xác.';
  let canonicalTagUrl = mainURL + '/about-us';

  return (
    <Layout
      title={pageTitle}
      description={pageDescription}
      {...props}
      // canonical={ mainURL + '/nguoi-dung/' + props.slug}
      path={'nguoi-dung'}
      // dataCompare={dataCompare}
      canonical={ mainURL + '/nguoi-dung/' + 'ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485' }
    >
      <h1>Hahaha Slug</h1>
      <Users />
    </Layout>
  );
}


// <Layout
//       title={metaTitle}
//       description={metaDescription}
//       {...props}
//       image={imageAvatar}
//       canonical={ mainURL + '/chu-dau-tu/' + props.slug}
//       path={'chu-dau-tu'}
//       dataCompare={dataCompare}
//     >
//       <DetailInvestor investor={investor} slug={props.slug} />
//     </Layout>

UserPage.getInitialProps = async function(props: QueryProp) {
  let projectSlug = props.query.slug;
  let projectDetail = await apolloClient.query({query: GET_PROJECT_BY_SLUG, variables: {
      "slug": projectSlug
    }}).then(response => {
    return response;
  });
  return {
    projectDetail: projectDetail,
    slug: projectSlug,
  };
};

export default UserPage;
