import React, { useState, useEffect } from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import {
  Avatar,
  Typography,
  Button,
  Hidden,
  IconButton,
  Snackbar,
  Tooltip,
  colors
} from '@material-ui/core';
import AddPhotoIcon from '@material-ui/icons/AddPhotoAlternate';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import ChatIcon from '@material-ui/icons/ChatOutlined';
import MoreIcon from '@material-ui/icons/MoreVert';
import FacebookIcon from '@material-ui/icons/Facebook';

const useStyles = makeStyles(theme => ({
  root: {},
  cover: {
    position: 'relative',
    height: 360,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    '&:before': {
      position: 'absolute',
      content: '" "',
      top: 0,
      left: 0,
      height: '100%',
      width: '100%',
      backgroundImage:
        'linear-gradient(-180deg, rgba(0,0,0,0.00) 58%, rgba(0,0,0,0.32) 100%)'
    },
    '&:hover': {
      '& $changeButton': {
        visibility: 'visible'
      }
    }
  },
  changeButton: {
    visibility: 'hidden',
    position: 'absolute',
    bottom: theme.spacing(3),
    right: theme.spacing(3),
    backgroundColor: colors.blueGrey[900],
    color: theme.palette.divider,
    [theme.breakpoints.down('md')]: {
      top: theme.spacing(3),
      bottom: 'auto'
    },
    '&:hover': {
      backgroundColor: colors.blueGrey[900]
    }
  },
  addPhotoIcon: {
    marginRight: theme.spacing(1)
  },
  container: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    padding: theme.spacing(2, 3),
    margin: '0 auto',
    position: 'relative',
    display: 'flex',
    flexWrap: 'wrap',
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column'
    }
  },
  avatar: {
    border: `2px solid ${theme.palette.divider}`,
    height: 120,
    width: 120,
    top: -60,
    left: theme.spacing(3),
    position: 'absolute'
  },
  details: {
    marginLeft: 136
  },
  actions: {
    marginLeft: 'auto',
    [theme.breakpoints.down('sm')]: {
      marginTop: theme.spacing(1)
    },
    '& > * + *': {
      marginLeft: theme.spacing(1)
    }
  },
  pendingButton: {
    color: theme.palette.divider,
    backgroundColor: colors.red[600],
    '&:hover': {
      backgroundColor: colors.red[900]
    }
  },
  personAddIcon: {
    marginRight: theme.spacing(1)
  },
  mailIcon: {
    marginRight: theme.spacing(1)
  }
}));

interface HeaderProps {
  className?:string,
}

const Header:React.FunctionComponent<HeaderProps> = (props:HeaderProps) => {
  const { className, ...rest } = props
  const classes = useStyles()
  const user = {
    name: 'Nguyễn Phi Hùng',
    bio: 'Chyên viên tư vấn',
    avatar: 'https://vcdn-giaitri.vnecdn.net/2020/01/23/C-OiQ-UUIAIycqR-1-1579796735_680x0.jpg',
    cover: 'https://react-material-kit.devias.io/images/covers/cover_2.jpg',
    connectedStatus: 'not_connected'
  }
  const [connectedStatus, setConnectedStatus] = useState(user.connectedStatus) // if rejected do not show the button
  const [openSnackbar, setOpenSnackbar] = useState(false)
  useEffect(() => {
    if (connectedStatus === 'pending') {
      setOpenSnackbar(true);
    }
  }, [connectedStatus]);
  const handleConnectToggle = () => {
    setConnectedStatus(connectedStatus =>
      connectedStatus === 'not_connected' ? 'pending' : 'not_connected'
    );
  }
  const handleSnackbarClose = () => {
    setOpenSnackbar(false);
  }
  return (
    <div>
      <div className={classes.cover} style={{ backgroundImage: `url(${user.cover})`}}>
      </div>
      <div className={classes.container}>
        <Avatar 
          alt="nhadatmoi.net" className={classes.avatar} 
          src={user.avatar} 
        />
        <div className={classes.details}>
          <Typography component="h2" gutterBottom variant="overline">
            {user.bio}
          </Typography>
          <Typography component="h1" variant="h4" >
            {user.name}
          </Typography>
        </div>
        <Hidden smDown>
          <div className={classes.actions}>
            <a rel="nofollow" title="Facebook" target="_blank" href="https://www.facebook.com/www.zozo.vn"> 
              <img width="40px" height="40px" alt="Facebook" src="https://zozo.vn/public/theme/circle/images/icons/fb-mau.svg"/> 
            </a>
            <a title="Trò chuyện thông qua Facebook Messenger" target="_blank" href="http://m.me/925790224164852?ref=zozovn"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/fbmessenger.svg" alt="btn-facebook"/>
            </a>
            <a title="Chat với chúng tôi qua Zalo" target="_blank" href="https://zalo.me/0936162822"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/zalo.svg" alt="btn-zalo"/>
            </a>
            <a title="Gọi ngay" href="tel:0936162822"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/call.svg" alt="btn-callnow"/>
            </a>
            <Button
              color="primary"
              variant="contained"
            >
              <ChatIcon className={classes.mailIcon} />
              Gửi liên hệ
            </Button>

          </div>
        </Hidden>

      </div>
    </div>
  )
}

export default Header;
