import gql from "graphql-tag";

const GET_CHANNEL_BY_SLUG = gql`
  query channel ($slug: String) {
    channel (slug: $slug) {
      id
      name
    }
  }
`;

 
const GET_MEMBER_CHANNEL_BY_ID = gql`
query channelMembers ($id: String!, $page: Int!, $limit: Int! ) {
  channelMembers (id: $id, page: $page, limit: $limit) {
    edges {
       node {
        avatar
        first_name
        last_name
        mobile
      }
    }
  }
}
`;

export {
  GET_CHANNEL_BY_SLUG,
  GET_MEMBER_CHANNEL_BY_ID
}
