import React from 'react';
import clsx from 'clsx';
import { Grid } from "@material-ui/core"
import { createStyles, Theme, makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import WcIcon from '@material-ui/icons/Wc';
import PhoneIcon from '@material-ui/icons/Phone';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import EmailIcon from '@material-ui/icons/Email';


const useStyles = makeStyles(theme => ({
    root: {
        width: '100%',
        color: "#aaa",
        backgroundColor: theme.palette.background.paper,
    },

    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    }
}));


interface InfoProps {
    className?: string
    infoUser: User
}

interface User {
    address: string
    avatar: string
    birthday: string
    email: string
    facebook: string
    first_name: string
    gender: string
    google_id: string
    id: string
    intro: string
    last_name: string
    phone: string
    mobile: string
    skype: string
    username: string
    viber: string
    website: string
    zalo: string
    created_at: string
    updated_at: string
}

const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
    const { className } = props
    const classes = useStyles()
    let userName = `${props.infoUser.first_name} ${props.infoUser.last_name}`
    return (
        <div className={clsx(classes.root, className)}>
            <Grid container spacing={2}>
                <Grid item xs={8} lg={8}>
                    <List component="nav" className={classes.root} dense={true} aria-label="mailbox folders">
                        <ListItem button divider={true}>
                            <ListItemIcon><AccountCircleIcon/></ListItemIcon>
                            <ListItemText primary={userName} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><WcIcon/></ListItemIcon>
                            <ListItemText primary={props.infoUser.gender ? props.infoUser.gender : "Đang cập nhật"} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><PhoneIcon/></ListItemIcon>
                            <ListItemText primary={props.infoUser.mobile ? props.infoUser.mobile : "Đang cập nhật"}  />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><LocationOnIcon/></ListItemIcon>
                            <ListItemText primary={props.infoUser.address ? props.infoUser.address : "Đang cập nhật"} />
                        </ListItem> <Divider />
                        <ListItem button divider={true}>
                            <ListItemIcon><EmailIcon/></ListItemIcon>
                            <ListItemText primary={props.infoUser.email ? props.infoUser.email : "Đang cập nhật"} />
                        </ListItem> <Divider/>
                    </List>
                </Grid>
               <Grid item xs={4}>

               </Grid>
           </Grid>
        </div>
    );
};


export default Info;
