import React from "react";
import clsx from "clsx";
import { Grid, Typography, Card, CardContent, CardActions } from "@material-ui/core";
import { createStyles, Theme, makeStyles } from "@material-ui/core/styles";
import { List, Avatar } from "@material-ui/core";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import Divider from "@material-ui/core/Divider";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import WcIcon from "@material-ui/icons/Wc";
import PhoneIcon from "@material-ui/icons/Phone";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import EmailIcon from "@material-ui/icons/Email";
import PhoneInTalkIcon from '@material-ui/icons/PhoneInTalk';
import { agency } from "../Constant"

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    color: "#aaa",
    marginTop: theme.spacing(3),
    backgroundColor: theme.palette.background.paper,
  },
  avatar: {
    margin: "10px auto",
    height: 80,
    width: 80,
  },
  posts: {
    marginTop: theme.spacing(2),
  },
  post: {
    marginBottom: theme.spacing(2),
  },
}));

interface InfoProps {
  className?: string;
  infoUser: User;
}

interface User {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}


const Info: React.FunctionComponent<InfoProps> = (props: InfoProps) => {
  const { className } = props;
  const classes = useStyles();
  let userName = `${props.infoUser.first_name} ${props.infoUser.last_name}`;
  return (
    <div className={clsx(classes.root, className)}>
      <Grid container direction="row" alignItems="center" spacing={1}>
        {agency.map((item: any, index: number) => {
          return (
            <Grid key={index} item lg={2} md={3} sm={4} xs={6}  style={{ margin: "10px 0px"}}>
              <div
                style={{
                  border: "2px solid #D9EDFE",
                  borderRadius: "4px",
                  height: "210px",
                  width: "100%",
                  textAlign: "center",
                }}
              >
                <Avatar alt="" className={classes.avatar} src={item.avatar} />
                <div style={{ height: "45px" }}>
                  <Typography style={{ color: "black" }} >{item.name}</Typography>
                </div>
                <a title="Gọi ngay" href="tel:0936162822">
                  <PhoneInTalkIcon />
                </a>
                <Typography>{item.phone}</Typography>
              </div>
            </Grid>
          );
        })}
      </Grid>
    </div>
  );
};

export default Info;
