import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';


const useStyles = makeStyles(theme => ({
  root: {},
  subheader: {
    flexWrap: 'wrap',
    display: 'flex',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface OverallReviewsProps {
  className?:string,
}
 
interface OverallReviewsProps {
  className?:string,
}

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  let reviews = [
    {
      id: "1",
      reviewer: {
        avatar: "https://zicxa.com/hinh-anh/wp-content/uploads/2019/07/T%E1%BB%95ng-h%E1%BB%A3p-h%C3%ACnh-%E1%BA%A3nh-g%C3%A1i-xinh-d%E1%BB%85-th%C6%B0%C6%A1ng-cute-nh%E1%BA%A5t-6.jpg",
        name: "Nguyễn Hồng Thúy"
      },
    rating: "rating",
    created_at: "2020-31-03",
    project: {
      title: "project title",
      price: "number price",
    },
    message: "message",
    currency: "currency",
    pricePerHour: 2,
    hours: 4,
    }, {
      id: "1",
      reviewer: {
        avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg",
        name: "Mai Trâm"
      },
    rating: "rating",
    created_at: "2020-31-03",
    project: {
      title: "project title",
      price: "number price",
    },
    message: "message",
    currency: "currency",
    pricePerHour: 2,
    hours: 4,
    }
  ]
  const [value, setValue] = React.useState<number | null>(2);
  let ratings= reviews.map(review => review.rating)
  const { className, ...rest } = props;
  let rating = 0;
  if (ratings.length > 0) {
    rating = ratings.reduce((prev:any, current:any) => prev + current, 0) / ratings.length;
  }
  const classes = useStyles();
  return (
    <div  className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        <Grid item>
          <Card className={clsx(classes.root, className)}>
            <CardContent>
              <Grid alignItems="center" container spacing={3}>
                <Grid item>
                  <Typography variant="h5">Đánh giá tổng quan</Typography>
                </Grid>
                <Grid className={classes.stars} item>
                  <div style={{ display: 'inline-flex', alignItems: 'center'}}>
                    <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                      <Rating
                        name="simple-controlled"
                        value={value}
                        onChange={(event, newValue) => {
                          setValue(newValue);
                        }}
                      />
                    </Box>
                  </div>
                </Grid>
                <Grid item>
                  <Typography color="textSecondary" variant="body2" >
                    {ratings.length} lượt đánh giá
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {reviews.map((item: any, index: number) => {
        <Grid container spacing={4}>
          <Grid item>
            <Card>
              <CardHeader 
                avatar={
                  <Avatar alt="Reviewer" src={item.reviewer.avatar} >
                    {item.reviewer.name}
                  </Avatar>
                }
                disableTypography
                subheader={
                  <div className={classes.subheader}>
                    <div className={classes.stars}>
                      <div style={{ display: 'inline-flex', alignItems: 'center'}}>
                        <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                          <Rating
                            name="simple-controlled"
                            value={value}
                            onChange={(event, newValue) => {
                              setValue(newValue);
                            }}
                          />
                        </Box>
                      </div>
                    </div>
                    <Typography variant="body2">
                        {/* Đánh giá bởi{' '} <b>{item.reviewer.name}</b> | 3 giờ trước */}
                        {/* {moment(reviews[0].created_at).fromNow()} */}
                    </Typography>
                  </div>
                }
              />
              <CardContent>
                <div className={classes.message}>
                  <Typography variant="subtitle2">Hùng thật sự rất tuyệt vời mỗi khi gặp khách hàng</Typography>
                </div>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      })}
      

      
      
      <br />
      <Card>
        <CardContent>
          <div style={{ display: 'inline-flex', alignItems: 'center'}}>
            <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
              <Rating
                name="simple-controlled"
                value={value}
                onChange={(event, newValue) => {
                  setValue(newValue);
                }}
              />
            </Box>
          </div>
          <TextField
            multiline={true}
            fullWidth={true}
            rowsMax={10}
            margin="dense"
            name="reviews"
            placeholder="Đánh giá ..."
            variant="outlined"
          />
        </CardContent>
      </Card>
    </div>
  );
};


export default Project;
