import React, {FunctionComponent, useEffect} from "react";
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries";
import Validator from "../../modules/validator";
import GridAdSellLeases from "../GridList/GridAdSellLeases";

interface ProjectProps {
  address: {
    text: string,
    ward: string,
    district: string,
    city: string
  }
}

interface AdSellLeasesListProps {
  node: {
    title: string,
    description: string,
    slug: string,
    created_at: string,
    updated_at: string,
    price: {
      unit: string
      value: string
    },
    avatar: {
      alt_text: string
      src: string
      description: string
    },
    address: {
      text: string,
      ward: string,
      district: string,
      city: string
    },
    contaact: {
      image: string
      name: string
      address: string
      mobile: string
      phone: string
      email: string
    },
    area: {
      unit: string
      value: string
    }
  }
}

const RelatedAdSellLeases: FunctionComponent<ProjectProps> = (props: ProjectProps) => {
  let districtId = props.address.district;
  let cityId = props.address.city;
  let filterObject =  {
    district: districtId,
    city: cityId
  };
  //Search Data AdsellLease
  const [searchDataAdSellLease, setSearchDataAdSellLease] = React.useState<AdSellLeasesListProps[]>([]);
  useEffect(() => {
    let dataFilter = JSON.stringify(filterObject);
    dataFilter = dataFilter.split('"').join("'");
    apolloClient.query({query: GET_AD_SELL_LEASES, variables: {
        "page": 1,
        "limit": 6,
        "filter": dataFilter
      }}).then(response => {
      if (!Validator.isBlank(response.data) && !Validator.isBlank(response.data.adSellLeases.edges)) {
        setSearchDataAdSellLease(response.data.adSellLeases.edges);
      }
    });
  });

  if (searchDataAdSellLease && searchDataAdSellLease.length > 0) {
    return (
      <>
        <div className="project-info">
          <h2 className="project-info-title text-active u-margin-bottom-small">Tin rao cùng khu vực</h2>
        </div>
        <div className={'project-body-ad-sell-lease'}>
          <GridAdSellLeases
            grid={{xl: 3, lg: 4, md: 6, sm: 6, xs: 12}}
            gridData={searchDataAdSellLease}
            className="grid__list"
          />
        </div>
      </>
    );
  } else {
    return (<></>);
  }

};

export default RelatedAdSellLeases;
