import React, { FunctionComponent } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Agency from "./Tabs/Agency";
import Rating from "./Tabs/Rating";
import Introduce from "./Tabs/Introduce";
import AdSellLeases from "./Tabs/AdSellLeases";
import Header from "./Header";
import Paper from "@material-ui/core/Paper";
import StyledCheckbox from "./StyledCheckbox";
import {
  Tabs,
  Tab,
  Divider,
  colors,
  Grid,
  Button,
  Icon,
  IconButton,
  Typography,
  Card,
  CardContent,
  CardActions,
  Avatar,
  Checkbox,
} from "@material-ui/core";
import { relatedChannel } from "./Constant";
import Page from "./Page";

const useStyles = makeStyles((theme) => ({
  root: {
    color: "#444"
  },
  rootTabs: {
    flexGrow: 1,
  },
  large: {
    width: theme.spacing(6),
    height: theme.spacing(6),
  },
  divider: {
    backgroundColor: colors.grey[300],
  },
  button: {
    border: "2px solid #D9EDFE",
    borderRadius: "4px",
    margin: "0px 0px 8px 0px",
  },
  checkbox: { 
    float: "right", 
    display: "inline" 
  },
  contentChannel: {
    width: "100%",
    height: "64px",
    border: "2px solid #D9EDFE",
    borderRadius: "4px",
    margin: "0px 0px 8px 0px",
  },
  title: {
    fontWeight: 900,
    padding: "0px 0px 8px 0px",
    color: "#444"
  },
  follow: { 
    color: "#aaa", 
    fontSize: "13px", 
    display: "inline"
  }
}));

interface IndexProps {
  comments: CommentsProp[];
  infoUser: UserProps;
  ratings: RatingsProp[];
}

interface CommentsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    like_number: string;
    created_at: string;
  };
}

interface UserProps {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  intro: string;
  id: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

interface RatingsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    rate: number;
    like_number: string;
    created_at: string;
  };
}

const Index: FunctionComponent<IndexProps> = (props: IndexProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("introduce");
  const [count, setCount] = React.useState<number>(0);
  const tabs = [
    { value: "ad", label: "Tin rao" },
    { value: "introduce", label: "Giới thiệu" },
    { value: "agency", label: "Môi giới" },
    { value: "rating", label: "Đánh giá" },
  ];

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value);
  };

  return (
    <Page title="Profile">
      <Header infoUser={props.infoUser} />
      <CardContent>
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Paper square className={classes.rootTabs}>
              <Tabs
                onChange={handleTabsChange}
                scrollButtons="auto"
                indicatorColor="primary"
                textColor="primary"
                value={itemTab}
              >
                {tabs.map((tab) => (
                  <Tab key={tab.value} label={tab.label} value={tab.value} />
                ))}
              </Tabs>
            </Paper>
            <Divider className={classes.divider} />
          </Grid>
          <Grid item xs={9}>
            <div className="body_container">
              {itemTab === "ad" && <AdSellLeases userId={props.infoUser.id} />}
              {itemTab === "introduce" && <Introduce infoUser={props.infoUser} />}
              {itemTab === "agency" && <Agency infoUser={props.infoUser} />}
              {itemTab === "rating" && <Rating />}
            </div>
          </Grid>
          <Grid item xs={3}>
            <Typography variant="h6" className={classes.title}>
              Kênh liên quan
            </Typography>
            {relatedChannel.map((item: any, index: number) => {
              if (index < count * 6 + 6) {
                return (
                  <div className={classes.contentChannel} >
                    <Grid
                      key={index}
                      container
                      alignItems="center"
                      justify="center"
                      spacing={4}
                      style={{ padding: "4px" }}
                    >
                      <Grid item xs={12}>
                        <div style={{ float: "left" }}>
                          <Avatar src={item.image} className={classes.large} alt="" />
                        </div>
                        <div style={{ margin: "0px 0px 0px 56px" }}>
                          <Typography
                            style={{
                              // display: "inline",
                              textOverflow: "ellipsis",
                              width: "240px",
                              whiteSpace: "nowrap",
                              overflow: "hidden",
                            }}
                          >
                            {item.title}
                          </Typography>

                          <Typography className={classes.follow}>
                            {item.numberfollow} Lượt theo dõi
                          </Typography>
                          {item.follow && (
                            <StyledCheckbox
                              defaultChecked={item.follow}
                              className={classes.checkbox}
                            />
                          )}
                        </div>
                        <div style={{ clear: "both" }}></div>
                      </Grid>
                    </Grid>
                  </div>
                );
              } else {
                return "";
              }
            })}
            <Grid container spacing={4}>
              <Grid item xs={12}>
                <Button onClick={() => setCount(count + 1)} fullWidth className={classes.button}>
                  Xem thêm
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </CardContent>
    </Page>
  );
};

export default Index;
