import React, {useEffect} from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/Project/ProjectMain";
import DetailInvestor from "../../components/Investor/DetailInvestor"
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASE_COMMENTS} from "../../graphql/comments/queries";
import {GET_USER_ID_BY_SLUG_AD} from "../../graphql/ad_sell_leases/queries"
import {GET_USER_BY_ID } from "../../graphql/user/queries/index"
import {GET_INVESTOR_BY_ID} from "../../graphql/investor/queries"
import StringProcess from "../../modules/stringProcess";
import "../../assets/scss/style.scss";
import {localStorageCompareKey, mainURL} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import {COUNT_VIEW_PROJECT} from "../../graphql/project/mutation";
import User from "../../components/Users/Index"
 
interface ProjectDetails {
  url: { query: { slug: string }},
  listComment: { data: { adSellLeaseComments: { edges: ListCommentProps[]}} },
  slug: string,
  userId: string
  infoUser: UserProps
}

interface QueryProp { 
  query: { slug: string } 
}

interface UserProps {
  address: String
  avatar: String
  birthday: String
  email: String
  facebook: String
  first_name: String
  gender: String
  google_id: String
  intro: String
  id: String
  last_name: String
  phone: String
  mobile: String
  skype: String
  username: String
  viber: String
  website: String
  zalo: String
  created_at: String
  updated_at: String
}

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

// @ts-ignore
const UserPage: NextPage<ProjectDetails> = (props: ProjectDetails) => {
  return (
    <Layout
      title={"metaTitle"}
      description={"metaDescription"}
      {...props}
      image={"https://www.w3schools.com/w3css/img_lights.jpg"}
      canonical={ mainURL + '/nguoi-dung/' + "ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485"}
      path={'nguoi-dung'}
      dataCompare={{}}
    >
      <User 
        listComment= {props.listComment.data.adSellLeaseComments.edges}
        userId={props.userId}
        infoUser={props.infoUser}
      />
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  let adSlug = props.query.slug;
  let filterObject =  {
    slug: adSlug,
  };
  let dataFilter = JSON.stringify(filterObject);
  let listComment = await apolloClient.query({query: GET_AD_SELL_LEASE_COMMENTS, variables: {
      "page": 1,
      "limit": 100,
      "filter": dataFilter
    }}).then(response => {
    return response;
  });
  let userId = await apolloClient.query({query: GET_USER_ID_BY_SLUG_AD, variables: {
    "slug": adSlug
  }}).then(response => { 
    return response.data.adSellLease.user
  })

  let infoUser =  await apolloClient.query({query: GET_USER_BY_ID, variables: {
    "id": userId
    }}).then(response => { 
      return response.data.user
  })

  return {
    listComment: listComment,
    slug: adSlug,
    userId: userId,
    infoUser: infoUser
  };
};

export default UserPage;
