import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { qualityTitle, extraQualityTitle, qualitys } from "./Constants"
import { makeStyles } from "@material-ui/core/styles"

const benefitStyles = makeStyles((theme) => ({
  benefit: {
    textAlign: "center"
  },
  titleFontWeight: {
    fontWeight: 900,
    fontSize: "30px",
    [theme.breakpoints.down("sm")]: {
      fontSize: "20px",
    }
  },
  titlEextraFontWeight: {
    fontWeight: 500,
    [theme.breakpoints.down("sm")]: {
      fontWeight: 400,
    }
  },
  benefitTitle: {
    padding: "8px",
  },
  iconBenefit: { 
    width: 100, 
    height: 100,
    padding: "12px",
    [theme.breakpoints.down("xs")]: {
      width: 50, 
      height: 50, 
      padding: "4px",
      margin: "4px 4px",
    },
  },
  iconMarginTopBenefit: {
    width: 100, 
    height: 100, 
    padding: "12px",
    [theme.breakpoints.down("xs")]: {
      width: 50, 
      height: 50, 
      padding: "0px 0px",
      marginTop: "4px",
    },
  },
  positionCenter: {
    width: 100, 
    height: 100, 
    margin: "0px auto",
    textAlign: "center",
    [theme.breakpoints.down("xs")]: {
      // float: "left",
      margin: "4px auto",
      width: 30, 
      height: 40, 
      // display: "inline"
    }
  },
  titleItemBenefit: { 
    fontWeight: 500, 
    textAlign: 'center',
    [theme.breakpoints.down("xs")]: {
      textAlign: "left",
      padding: "4px",
      // display: "inline"
    },
  },
  contentItemBenefit: { 
    verticalAlign: "middle", 
    textAlign: "justify",
  },
}));

const Benefit = () => {
  const classes = benefitStyles();
  return (
    <div className={"mission-area body-introduce"} >
      <Grid container alignItems="flex-start" direction="row" justify="center" spacing={10} className={classes.benefit}>
        <Grid item xs={12}>
          <div className={classes.benefitTitle} >
            <Typography className={classes.titleFontWeight}>
              {qualityTitle}
            </Typography>
            <Typography className={classes.titlEextraFontWeight}>
              {extraQualityTitle}
            </Typography>
          </div>
        </Grid>
      </Grid>
      <Grid container alignItems="flex-start" direction="row" justify="center" spacing={2}>
        {qualitys.map((element: any, index: number) => {
          return (
            <Grid item xs={12} sm={6} md={4} >
              <div className={classes.positionCenter}>
                <img src={element.icon} className={ index === 0 ? classes.iconBenefit : classes.iconMarginTopBenefit }/>
              </div>
              <Typography variant="h6" className={classes.titleItemBenefit}>
                {element.title}
              </Typography>
              <Typography variant="h6" className={classes.contentItemBenefit}>
                {element.content.map((e: any, count: number) => {
                  if(count === 0){
                    return <Typography>{e.item}</Typography>
                  } else {
                    return <Typography>- {e.item}</Typography> 
                  }
                })}
              </Typography>
            </Grid>
          )
        })}
      </Grid>
     
    </div>    
  );
};

export default Benefit;
