const defaultUser = {
  _id: "",
  email: "",
  avatar: "",
  first_name: "",
  last_name: ""
};
const useStyles = makeStyles((theme: Theme) => ({
  toolbar: {
    borderBottom: `1px solid ${theme.palette.divider}`,
  },
  toolbarTitle: {
    flex: 1,
  },
  toolbarItem: {
    marginRight: theme.spacing(1)
  },
  typography: {
    padding: theme.spacing(2),
  },
  inline: {
    display: "inline"
  }
}));

interface LayoutProps {
  user?: {
    email: string,
    _id: string,
    first_name: string,
    last_name: string,
    avatar: string
  },
  statusModal: boolean,
  updateModal: (data: boolean) => void;
  path: string,
  canonical: string,
  dataQuery?: {
    title: string,
    name: string
  },
  dataCompare?: any
}

