            <Link href="/nha-dat-ban"><a><Button className={"header_button _empty" + ((props.path === 'tin-rao' || props.path === 'nha-dat-ban')?' active':'')} variant="outlined">
              Tin rao
            </Button></a></Link>
            <Link href="/du-an-bat-dong-san"><a><Button className={"header_button _empty" + ((props.path === 'du-an' || props.path === 'du-an-bat-dong-san')?' active':'')} variant="outlined">
              Dự án
            </Button></a></Link>
          </Hidden>
          <Hidden mdUp>
            <IconButton
              edge="start"
              className="header_button _menu"
              aria-label="open drawer"
              onClick={toggleDrawer('right', true)}
            >
              <MenuIcon/>
            </IconButton>
          </Hidden>
        </div>
        <Drawer anchor="right" open={state.right} onClose={toggleDrawer('right', false)}>
          <div className="header__menu_mobile">
            <Typography className="header__menu_mobile__body">
              {
                userInfo._id &&
                <ListItem alignItems="flex-start">
                  <ListItemAvatar className="header__menu_mobile__avatar">
                    <Avatar alt={userInfo.first_name} src={userInfo.avatar}/>
                  </ListItemAvatar>
                  <ListItemText
                    className="header__menu_mobile__title"
                    primary={`${userInfo.first_name} ${userInfo.last_name}`}
                    secondary={
                      <React.Fragment>
                        <Typography
                          component="span"
                          variant="body2"
                          color="textPrimary"
                        >
                          {userInfo.email}
                        </Typography>
                      </React.Fragment>
                    }
                  />
                </ListItem>
              }

              {
                !userInfo._id &&
                <><a href="https://manage.nhadatmoi.net/settings">
                  <ListItem alignItems="flex-start">
                    <ListItemAvatar className="header__menu_mobile__avatar">
                      <Avatar alt="Khách ghé thăm" src="https://static.nhadatmoi.net/default/user.png"/>
                    </ListItemAvatar>
                    <ListItemText
                      className="header__menu_mobile__title"
                      primary="Chào mừng Khách"
                      secondary={
                        <React.Fragment>
                          <Typography
                            component="span"
                            variant="body2"
                            className={classes.inline}
                            color="textPrimary"
                          >
                            Nhà Đất Mới
                          </Typography>
                        </React.Fragment>
                      }
                    />
                  </ListItem>
                </a></>
              }
              <Divider/>

              <Link href="/nha-dat-ban"><a>
                <ListItem button>
                  <ListItemIcon>
                    <ListAlt/>
                  </ListItemIcon>
                  <ListItemText primary="Tin rao"/>
                </ListItem>
              </a>
              </Link>

              <Link href="/du-an-bat-dong-san"><a>
                <ListItem button>
                  <ListItemIcon>
                    <FilterVintage/>
                  </ListItemIcon>
                  <ListItemText primary="Dự án"/>
                </ListItem></a></Link>

              {
                userInfo._id &&
                <a href={"https://manage.nhadatmoi.net/asl/create"}>
                  <ListItem button>
                    <ListItemIcon>
                      <AddCircle/>
                    </ListItemIcon>
                    <ListItemText primary="Đăng tin"/>
                  </ListItem>
                </a>
              }

              {
                !userInfo._id &&
                <ListItem button onClick={handleClickOpen}>
                  <ListItemIcon>
                    <AddCircle/>
                  </ListItemIcon>
                  <ListItemText primary="Đăng tin"/>
                </ListItem>
              }
              {
                !userInfo._id &&
                <ListItem button onClick={handleClickOpen}>
                  <ListItemIcon>
                    <ArrowForward/>
                  </ListItemIcon>
                  <ListItemText primary="Đăng nhập"/>
                </ListItem>
              }

              {
                userInfo._id &&
                <a href="https://manage.nhadatmoi.net">
                  <ListItem button>
                    <ListItemIcon>
                      <GroupAddIcon/>
                    </ListItemIcon>
                    <ListItemText primary="Nâng cấp"/>
                  </ListItem>
                </a>
              }

              {
                userInfo._id &&
                <a href="https://manage.nhadatmoi.net/asl/list">
                  <ListItem button>
                    <ListItemIcon>
                      <ListIcon/>
                    </ListItemIcon>
                    <ListItemText primary="Quản lý tin rao"/>
                  </ListItem>
                </a>
              }

              {
                userInfo._id &&
                <Divider/>
              }
              {
                userInfo._id &&
                <a href="https://manage.nhadatmoi.net/settings">
                  <ListItem button>
                    <ListItemIcon>
                      <SettingsIcon/>
                    </ListItemIcon>
                    <ListItemText primary="Cài đặt"/>
                  </ListItem>
                </a>
              }
              {
                userInfo._id &&
                <ListItem button onClick={() => logout()}>
                  <ListItemIcon>
                    <ExitToAppIcon/>
                  </ListItemIcon>
                  <ListItemText primary="Đăng xuất"/>
                </ListItem>
              }
            </Typography>
          </div>
        </Drawer>
        </Grid>
      </Grid>
      {
        dataCompare && !Validator.isBlank(dataCompare) && dataCompare.length !== 0 &&
        <div className="footer_compare">
          <div className={'container-big'}>
            <div className={"footer_compare__title"}>So sánh</div>

            <div className={"footer_compare__content"}>
              {
                dataCompare.map((compareDataItem: any, index: number) => {
                  return (
                    <div className={'footer_compare__content__item'} key={'data_compare_' + index}>
                      <div className={'footer_compare__content__item__avatar'}>
                        <img src={compareDataItem.avatar} alt={compareDataItem.title}/>
                      </div>
                      <div className={'footer_compare__content__item__title'}>
                        {StringProcess.truncateString(compareDataItem.title, 60, '...')}
                      </div>
                    </div>
                  );
                })
              }
            </div>
            <div className={"footer_compare__button"}>
              <Button className={'btn_compare'}>SO SÁNH</Button>
              <Button className={'btn_cancel'} onClick={() => clickCancelCompare()} >HUỶ BỎ</Button>
            </div>
          </div>
        </div>
      }
      <div className="header_background"/>
      {
        (props.path === '' || props.path === 'nha-dat-ban' || props.path === 'du-an-bat-dong-san') &&
        <>
          {
            userInfo._id &&
            <a href={"https://manage.nhadatmoi.net/asl/create"}>
              <Fab aria-label={"Đăng bài mới"} className={"header_button__float"}>
                <AddIcon/>
              </Fab>
            </a>
          }
          {
            !userInfo._id &&
            <Fab aria-label={"Đăng bài mới"} className={"header_button__float"} onClick={handleClickOpen}>
              <AddIcon/>
            </Fab>
          }
        </>
      }
    </div>
  );
};

export default HeaderPage;
