import React, { FunctionComponent, useEffect} from 'react';
import {makeStyles} from '@material-ui/core/styles'
import Info from './Tabs/Info'
import Reviews from "./Tabs/Reviews"
import Ad from "./Tabs/Ad"
import Header from "./Header"
import Paper from '@material-ui/core/Paper';
import {Tabs, Tab, Divider, colors} from '@material-ui/core';
import {apolloClient} from "../../utils";
import {GET_USER_BY_ID } from "../../graphql/user/queries/index"
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries"
import Page from "./Page";
 
const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  inner: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    margin: '0 auto',
    padding: theme.spacing(3)
  },
  divider: {
    backgroundColor: colors.grey[300]
  },
  content: {
    marginTop: theme.spacing(3)
  }
}));

interface ProfileProps {
  comments: CommentsProps[]
  userId: string
  user: UserProps
}

interface CommentsProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface UserProps {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  intro: string
  id: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

const Index: FunctionComponent<ProfileProps> = (props: ProfileProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("ad")
  const [user , setUser] = React.useState();
  const tabs = [
    {value: 'info', label: 'Thông tin'},
    {value: 'ad', label: 'Tin rao'},
    {value: 'reviews', label: "Đánh giá"}
  ]

  useEffect(() => {
    if(props.userId) {
      apolloClient.query({query: GET_USER_BY_ID, variables: {
        "id": props.userId,
      }}).then(response => { 
        setUser(response.data.user)
      })
    }
  }, [])

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }

  console.log("peops", props)

  return (
    <Page title="Profile">
      <Header user={props.user}/>
      <div className={classes.inner}>
        <Paper square className={classes.rootTabs}>
        <Tabs
          onChange={handleTabsChange}
          scrollButtons="auto"
          indicatorColor="primary"
          textColor="primary"
          value={itemTab}
        >
          {tabs.map(tab => (
            <Tab key={tab.value} label={tab.label} value={tab.value} />
          ))}
        </Tabs>
        </Paper>
        <Divider className={classes.divider}/>
        <div className={classes.content}>
          {itemTab === 'info' && <Info user={props.user} />}
          {itemTab === 'ad' && <Ad userId={props.userId} />}
          {/* {itemTab === 'reviews' && <Reviews comments={props.comments} userId={props.userId} />} */}
        </div>
      </div>
    </Page>
  )
}

export default Index;
