import React, { FunctionComponent } from 'react';
// import {Redirect, BrowserRouter as Router, useHistory, useParams} from "react-router-dom"
import {makeStyles} from '@material-ui/core/styles'
import Timeline from './Tabs/Timeline'
import Header from "./Header"
import {Tabs, Tab, Divider, colors} from '@material-ui/core';

import Page from "./Page";
// import {
  // Header, 
  // Timeline, 
  // Connections, 
  // Projects, 
  // Reviews
// } from './components';

const useStyles = makeStyles(theme => ({
  root: {},
  inner: {
      width: theme.breakpoints.values.lg,
      maxWidth: '100%',
      margin: '0 auto',
      padding: theme.spacing(3)
  },
  divider: {
      backgroundColor: colors.grey[300]
  },
  content: {
      marginTop: theme.spacing(3)
  }
}));

interface ProfileProps {

}

interface ParamType {
  id:any,
  tab:any
}

const Index: FunctionComponent<ProfileProps> = (props: ProfileProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = useState("timeline")

  const tabs = [
    {value: 'timeline', label: 'Timeline'},
    {value: 'connections', label: 'Connections'},
    {value: 'projects', label: 'Projects'},
    {value: 'reviews', label: 'Reviews'}
  ]

  const handleTabsChange = (event: any, value: any) => {
    // itemTab = value
  }
  console.log("itemTab", itemTab)
  return (
    <div>
      <Page title="Profile">
        <Header/>
        <div className={classes.inner}>
          <Tabs
            onChange={handleTabsChange}
            scrollButtons="auto"
            value={"timeline"}
            variant="scrollable"
          >
            {tabs.map(tab => (
              <Tab
                  key={tab.value}
                  label={tab.label}
                  value={tab.value}
              />
            ))}
          </Tabs>
          <Divider className={classes.divider}/>
          <div className={classes.content}>
              {itemTab === 'timeline' && <Timeline/>}
              {itemTab === 'connections' && <Connections/>}
              {itemTab === 'projects' && <Projects/>}
              {itemTab === 'reviews' && <Reviews/>}
          </div>
        </div>
      </Page>
    </div>
  )
}


export default Index
