import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { postsInfo, introduceStyles } from "./Constants";
import { makeStyles } from "@material-ui/core/styles";

const Introduce = () => {
  const classes = introduceStyles();
  const renderContent = (element: any) => {
    return (
      <Grid item md={6} >
        <div>
          <Typography variant="h5" className={classes.introTitle} >
            {element.title}
          </Typography>
          {element.contents.content.map((e: any, count: number) => 
            <Typography key={count} className={classes.introContent}>{e.item}</Typography>
          )}
          {element.contents.contentDetail.map((e: any, count: number) => 
            <Typography key={count} className={classes.introContentDetail}>- {e.item}</Typography>
          )}
        </div>
        <div style={{ width: "25px", height: "40px", border: "2px solid orange", textAlign: "center", margin: "0 auto" }}>
                        
        </div>
      </Grid>
    )
  };
  
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <div className={classes.sizeImage}>
          <img src={element.image} className={classes.image} />
        </div>
      </Grid>
    )
  };

  return (
    <div className={"mission body"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div key={index}>
            { element.type === 1 
              ? <Grid container direction="row" justify="center" spacing={4} alignItems={"center"}>
                  {renderContent(element)}
                  {renderImage(element)}
                </Grid>
              : <Grid container direction="row" justify="center"  spacing={4} alignItems={"center"}>
                  <Hidden smDown>
                    <>
                      {renderImage(element)}
                      {renderContent(element)}
                    </>
                  </Hidden>
                  <Hidden mdUp>
                    <>
                      {renderContent(element)}
                      {renderImage(element)}
                    </>
                  </Hidden>
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Introduce;
