import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography } from '@material-ui/core';


const useStyles = makeStyles(theme => ({
    root: {},
    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    },
    stars: {
        display: 'flex',
        alignItems: 'center'
    },
    rating: {
        marginLeft: theme.spacing(2),
        fontWeight: theme.typography.fontWeightBold
    }
}));

interface OverallReviewsProps {
    className?:string,
    ratings:any[],
}

interface ListProjectProps {
    className ?: string,
    list_project: ProjectProps[],
}

interface ProjectProps  {
    node: {
        title: string,
        slug: string,
        created_at: string,
        updated_at: string,
        price: {
          unit: string
          value: string
        },
        avatar: {
          alt_text: string
          src: string
          description: string
        },
        address: {
          text: string,
          ward: string,
          district: string,
          city: string
        },
        contact: {
          image: string,
          name: string,
          address: string,
          mobile: string,
          phone: string,
          email: string,
        }
        area: {
          unit: string
          value: string
        }
    },
}

interface PostType {
    id: any,
    author:{
        name:string,
        avatar:string,
    },
    created_at:string,
    message:string,
    media:string,
    avatar:string,
    name:string,
    comments:{
        id:any,
        author:{
            avatar:string,
            name:string
        }
        created_at:string,
        message:string,
    }[],
    liked:boolean,
    likes:number,
}

interface OverallReviewsProps {
    className?:string,
    ratings?:any[],
  }

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const { ratings, className, ...rest } = props;
  let rating = 0;
  if (ratings.length > 0) {
      rating = ratings.reduce((prev:any, current:any) => prev + current, 0) / ratings.length;
  }
  const classes = useStyles();
  return (
    <div {...rest} className={clsx(classes.root, className)}>
      <Card {...rest} className={clsx(classes.root, className)}>
        <CardContent>
            <Grid alignItems="center" container spacing={3}>
            <Grid item>
                <Typography variant="h5">Overall Reviews</Typography>
            </Grid>
            <Grid className={classes.stars} item >
                <ReviewStars value={rating} starCount={5}/>
                <Typography className={classes.rating} variant="h6" >
                {rating}
                </Typography>
            </Grid>
            <Grid item>
                <Typography color="textSecondary" variant="body2" >
                {ratings.length} reviews in total
                </Typography>
            </Grid>
            </Grid>
        </CardContent>
      </Card>
        {/* */}
    </div>
  );
};


export default Project;
