import React from 'react';
import Popper, { PopperPlacementType } from '@material-ui/core/Popper';
import { createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import { 
  Grid, Button, InputBase, Fade,
  Typography, Paper, Card, CardHeader, CardContent,
  CardActionArea, CardMedia, Avatar, TextField,
  Badge, Divider, Tooltip, Input, Chip, IconButton
} from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';
import TagFacesIcon from '@material-ui/icons/TagFaces';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import CloseIcon from '@material-ui/icons/Close';
import SendIcon from '@material-ui/icons/Send';
import axios, {Method} from "axios";
import {apiBaseURL, apiGraphQLURL} from "../../constants";
import "cross-fetch/polyfill";
import ApolloClient from "apollo-boost";
const client = axios.create({ baseURL: apiBaseURL, timeout: 30000 });
import moment from "moment"
import 'moment/locale/es'   
moment.locale('vi')
import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles';
import { 
  dataEmoji,  arrChat, giphyDefault, theme, useStyles,
  themeTextfieldAdmin, themeTextfieldUser, StyledBadge
} from "./constant"
import { create } from 'jss';
import rtl from 'jss-rtl';
import { StylesProvider, jssPreset } from '@material-ui/core/styles';
const jss = create({ plugins: [...jssPreset().plugins, rtl()] });

export default function PositionedPopper() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState<HTMLButtonElement | null>(null);
  const [open, setOpen] = React.useState(false);
  const [placement, setPlacement] = React.useState<PopperPlacementType>();
  const [online, setOnline] = React.useState<boolean>(true)
  const [internet, setInternet] = React.useState<boolean>(false)
  const [texting, setTexting] = React.useState<boolean>(false)
  const [giphyData, setGiphyData] = React.useState<any>()
  const [openEmojiGiphy, setOpenEmojiGiphy] = React.useState<boolean>(false)
  const [indexEmoji, setIndexEmoji] = React.useState<number>()
  const [valueSearchGiphy, setValueSearchGiphy] = React.useState("")
  const [loading, setLoading] = React.useState(false)
  const [completed, setCompleted] = React.useState(1)
  let   [valueInput, setValueInput] = React.useState("")
  
  const onCloseChat = () => {
    setOpen(false)
  }
  const setOnKeyPress = (event: any) => {
    setTexting(true)
  }
  const setOnBlur = (event: any) => {
    setTexting(true)
  }
  const setOnKeyDown = (event: any) => {
    if(event.keyCode == 13){
      arrChat.push({ message: event.target.value, type: 'user', image: { url: "", width: "", height: "" }, date: String(new Date().getTime()) })
      setValueInput("")
    }
  }
  const senMesssage = () => {
    arrChat.push({ message: valueInput, type: 'user', image: { url: "", width: "", height: "" }, date: String(new Date().getTime()) })
    setValueInput("")
  }

  

  const renderHeader = () => {
    return (
      <CardHeader
        className={classes.hearder}
        avatar={
          <StyledBadge
            overlap="circle"
            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
            variant= { online ? "dot" : "standard" }
          >
            <Avatar 
              className={classes.sizeAvatarSmall} 
              alt="nhadatmoi.net" 
              src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
            />
          </StyledBadge>
        }
        title="Nguyễn Thanh Huyền"
        action={
          <IconButton color='inherit' aria-label="settings" onClick={() => onCloseChat()} >
            <CloseIcon/>
          </IconButton>
        }
      />
    )
  }
  const setChangeInput = (value: any) => {
    setValueInput(value)
  }
  const searchGiphy = async (event: any)=>  { 
    setValueSearchGiphy(event.target.value)
    let url = `https://api.giphy.com/v1/gifs/search?api_key=UgTMyvovmg74m8fPN6qYiB623nwS1IPc&q=${event.target.value}&limit=20&offset=0&rating=G&lang=vi`
    let getData = await axios.get(url, {})
      .then(function (response) {
        setGiphyData(response.data.data)
      })
  }
  let dataSearch: any = []
  // image: {
  //   url: 'https://media3.giphy.com/media/mlvseq9yvZhba/giphy.gif?cid=13984785c0b0951827d991e0244802c1aa7a37da3f7842a2&rid=giphy.gif',
  //   height: '220',
  //   width: "180"
  // }
  // let dataSearch = giphyDefault
  if(Array.isArray(giphyData)){
    giphyData.map((item: any, index: number) => {
      // dataSearch.push({ image:  item.images.original.url })
      dataSearch.push({ 
        image: {
          url: item.images.fixed_width.url,
          height: item.images.fixed_width.height,
          width: item.images.fixed_width.width,
        }})
    })
  }
  console.log("dataSearch", dataSearch)

  const setItemGiphy = (index: number, name: string) => {
    setOpenEmojiGiphy(!openEmojiGiphy)
    if(name == "giphyDefault") {
      arrChat.push({ 
        message: "", 
        type: 'user', 
        image: {
          url: giphyDefault[index].image.url,
          width: giphyDefault[index].image.width,
          height: giphyDefault[index].image.height,
        },
        date: String(new Date().getTime()) 
      })
    } else if ( name == "dataSearch") {
      arrChat.push({ 
        message: "", 
        type: 'user', 
        image: {
          url: dataSearch[index].image.url,
          width: dataSearch[index].image.width,
          height: dataSearch[index].image.height,
        },
        date: String(new Date().getTime())
      })
    }
  }

  const onClickEmoji = (index: number) => {
    valueInput = valueInput +  dataEmoji[index].native
    setValueInput(valueInput)
  }

  const renderEmojiGiphy = () => {
    return (
      <div className={classes.emojiGiphy}>
        <div className={classes.marinButtonEmojiGiphy}>
          <button 
            className={classes.buttonEmojiGiphy} 
            onClick={() => setViewEmoji(true)} 
            style={{ backgroundColor: !viewEmoji ? "#aaa" : "" }}
          > Biểu tượng</button>
          <button 
            className={classes.buttonEmojiGiphy} 
            onClick={() => setViewEmoji(false)} 
            style={{ backgroundColor: viewEmoji ? "#aaa" : "" }}
          > Ảnh động</button>
        </div>
        { !viewEmoji && <Paper component="form" className={classes.rootPaper}>
            <IconButton type="submit" className={classes.iconButton} aria-label="search">
              <SearchIcon />
            </IconButton>
            <InputBase
              onChange={(e) => searchGiphy(e)}
              className={classes.inputBaseGiphy}
              placeholder="Tìm kiếm"
            />
          </Paper>}
        { viewEmoji 
          ? <div className={classes.overflowHeightEmoji}>
            {dataEmoji.map((item: any, index: number) => {
              return (
                <div 
                  onClick={() => onClickEmoji(index)} 
                  key={index} className={classes.itemEmoji}
                >{item.native}</div>
              )
            })}
          </div>
          : <div className={classes.overflowHeightGiphy}>
              {valueSearchGiphy 
                ? dataSearch.map((item: any, index: number) => {
                    return (
                      <div className={classes.dispalyImageGiphy} >
                        <img 
                          className={classes.imgListGiphy} 
                          alt="giphy" 
                          src={item.image.url} 
                          onClick={() => setItemGiphy(index, "dataSearch")} 
                        />
                      </div>
                    )
                  })
                : giphyDefault.map((item: any, index: number) => {
                    return (
                      <div className={classes.dispalyImageGiphy} >
                        <img 
                          className={classes.imgListGiphy} 
                          alt="giphy" 
                          src={item.image.url} 
                          onClick={() => setItemGiphy(index, "giphyDefault")} 
                        />
                      </div>
                    )
                  })
              }
            </div>
        }
      </div>
    )
  }

  
  const renderContentChat = () => {
    return (
      <div className={classes.overflowHeightEmojiGiphy}>
        <Typography align="center" className={classes.textSizeNewDate}>
          {moment().format('LLL')}
        </Typography>
        <Divider />
        {arrChat.map((item: any, index: number) => {
          let checkNewDate = false
          let setViewAvatar = false
          let oldDate = index > 0 ? moment(Number(arrChat[index - 1].date)).format('YYYY-MM-DD') : ''
          let newDate = moment(Number(arrChat[index].date)).format('YYYY-MM-DD')
          if(index > 0 && (oldDate !== newDate)) { 
            checkNewDate = true
          }
          
          if(item.type === 'admin' && index > 0) {
            if((index === arrChat.length) || (arrChat[index - 1].type != arrChat[index].type) || (oldDate !== newDate)){
              setViewAvatar = true
            }
          } else if(item.type === 'admin' && index === 0 ) {
            setViewAvatar = true
          }
          return (
            <div key={index} style={{ display: "block" }}>
              { checkNewDate && <Typography align="center" className={classes.textSizeNewDate}>
                {moment(Number(item.date)).format("LLL")} <Divider /></Typography>}
              { item.type === 'admin' 
              ? <ThemeProvider theme={themeTextfieldAdmin}>
                  <div className={classes.viewAvatarChatContent}>
                    {
                      setViewAvatar 
                      ? <Avatar 
                          className={classes.avatarAdminText} 
                          alt="nhadatmoi.net" 
                          src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
                        />
                      : ""
                    }
                  </div>
                  <div className={classes.textFieldChatContent}>
                    {
                      item.image.url 
                      ? <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                          <div className={classes.viewContentImageGiphy} style={{ width: `${item.image.width}px`, height: `${item.image.height}px`}} >
                            <img 
                              className={classes.imgGiphy} 
                              alt="giphy" 
                              src={item.image.url} 
                            />
                          </div>
                        </Tooltip>
                      : <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                          <div className={classes.textAdminLeft}>
                            <TextField 
                              fullWidth={item.message.length > 12 ? true : false}
                              label=""
                              className={classes.textAdmin}
                              value={item.message.trim()}
                              margin="dense"
                              id="outlined-basic" 
                              multiline={true}
                              InputProps={{
                                readOnly: true
                              }}
                              variant="outlined" 
                            />
                          </div>
                        </Tooltip>
                    }
                  </div>
                </ThemeProvider>
              : <ThemeProvider theme={themeTextfieldUser}>
                  {
                    item.image.url
                    ? <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                        <div className={classes.viewContentImageGiphy} style={{ width: `${item.image.width}px`, height: `${item.image.height}px`}} >
                          <img 
                            className={classes.imgGiphy} 
                            alt="giphy" 
                            src={item.image.url} 
                          />
                        </div>
                      </Tooltip>
                    : <Tooltip title={moment(Number(item.date)).fromNow()} placement="top">
                        <div 
                          className={classes.textFieldUserChatContent}
                          style={{ textAlign: "right" }}
                          dir={`${item.message.length < 20 ? 'rtl' : ""}`}
                        >
                          <TextField 
                            fullWidth={item.message.length > 15 ? true : false}
                            label=""
                            className={classes.textUser}
                            value={item.message.trim()}
                            margin="dense"
                            id="outlined-basic" 
                            multiline={true}
                            InputProps={{
                              readOnly: true
                            }}
                            variant="outlined" 
                          />
                        </div>
                      </Tooltip>
                  }
                </ThemeProvider>
              }
            </div>
          )
        })}
        {openEmojiGiphy && renderEmojiGiphy()}
        {!internet 
          ? <p className={classes.errInternet}>
              <u>Không thể gửi tin nhắn này. Kiểm tra kết nối internet và thử lại</u>
            </p>
          : ""
        }
        {texting && <p className={classes.texting}>Đang nhập ...</p>}
      </div>
    )
  }
  
  const [viewEmoji, setViewEmoji] = React.useState(false)
  const onClickOpenEmojiGiphy = () => {
    setOpenEmojiGiphy(!openEmojiGiphy) 
    setViewEmoji(true)
  }

  const apiBaseURL = "https://graph.nhadatmoi.net";
  const googleLogin = `${apiBaseURL}/auth/google`;
  const apiGraphQLURL = `${apiBaseURL}/graphql`;
  const apiUpload = `${apiBaseURL}/upload_image`;

  const api = (method: Method, url: string, data?: any) => client
    .request(
    { data: JSON.stringify(data), 
      headers: { "Accept": "application/json","Content-Type": "application/json",
    }, method, url,  withCredentials: true,
  }).then((response: any) => response.data);

 
  const upLoadImage = (files: any) => {
    const reader = new FileReader;
    let file = files.target.files[0]
    reader.readAsDataURL(file)
    reader.onload = () => {
      setLoading(true);
      setCompleted(0);
      api("POST", `${apiUpload}`, { 
        base64image: reader.result, 
        fileName: (new Date()).getTime() + "" + file.name,
        typeUpload: "ad_sell_lease",
      })
      .then(function (res) {
        console.log("res", res)
        // getFile(res.location, file.name, file.size);
        setLoading(false);
      })
      .catch(function (err) {
        alert('HTTP Error: ' + err.message);
      });
    };
  }
 
  const renderInput = () => {
    return (
      <Paper component="form" className={classes.inputBase}>
        <ThemeProvider theme={theme}>
          <Input 
            fullWidth
            // disabled={internet}
            margin="dense"
            id="outlined-basic" 
            autoFocus={true}
            // value={valueInput}
            value={valueInput}
            multiline={true}
            onChange={(e) => setChangeInput(e.target.value)}
            onKeyPress={(e) => setOnKeyPress(e)}
            onBlur={(e) => setOnBlur(e)}
            onKeyDown={(e) => setOnKeyDown(e)}
            placeholder="Nhập tin nhắn ..."
            autoComplete="false"
          />
          { openEmojiGiphy 
            ? <Tooltip title="Chèn biểu tượng" placement="top"> 
                <ExpandMoreIcon 
                  className={classes.sizeIcon}
                  onClick={onClickOpenEmojiGiphy}
                  color="primary"
                />
              </Tooltip> 
            : <Tooltip title="Chèn biểu tượng" placement="top"> 
                <TagFacesIcon 
                  className={classes.sizeIcon}
                  onClick={onClickOpenEmojiGiphy}
                  color="primary"
                />
              </Tooltip>
          }
          { valueInput
            ? <Tooltip title="Gửi tin nhắn" placement="top">
                <SendIcon 
                  onClick={senMesssage} 
                  className={classes.sizeIcon} color="primary"
                />
              </Tooltip>
            : <Tooltip title="Thêm tập tin" placement="top">
                <div className={classes.inputWrapper}> 
                  <AddCircleOutlineIcon 
                    className={classes.sizeIcon} 
                    color="primary"
                  /> 
                  <input type="file" className={classes.fileInput}  onChange={upLoadImage} /> 
                </div>
              </Tooltip>
          }
        </ThemeProvider>
      </Paper>
    )
  }
 
  const viewChat = () => {
    return (
      <Popper 
        open={open} 
        anchorEl={anchorEl}
        placement={placement} 
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={300}>
            <Paper>
              <div className={classes.positionSizeViewChat} >
                {renderHeader()}
                {renderContentChat()}
                {renderInput()}
              </div>
            </Paper>
          </Fade>
        )}
      </Popper>
    )
  }

  const handleClick = (newPlacement: PopperPlacementType) => {
    setOpen(true)
    setPlacement(newPlacement);
  }

  const renderAvatar = () => {
    return (
      <div className={classes.positionAvatar}>
        <StyledBadge
          overlap="circle"
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'right',
          }}
          variant= { online ? "dot" : "standard" }
        >
          <Avatar 
            onClick={() => handleClick("top-start")}
            className={classes.large} 
            alt="nhadatmoi.net" 
            src="https://image.crisp.chat/avatar/operator/da8d4459-5ea3-4119-bca1-9170f49ee69c/240/?1585999578742&quot"
            // src="https://file.vforum.vn/hinh/2018/03/hinh-mau-trang-tinh-tron-hinh-nen-anh-dai-dien-anh-bia-toan-mau-trang1.png"
          />
        </StyledBadge>
      </div>
    )
  }

  return (
    <div className={classes.root}>
      {viewChat()}
      <Grid container justify="center">
        <Grid item>
          { !open && renderAvatar()}
        </Grid>
      </Grid>
    </div>
  );
}
