import React from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import {apolloClient} from "../../../utils"
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import {GET_AD_SELL_LEASE_COMMENTS} from "../../../graphql/comments/queries";
import {GET_USER_BY_ID } from "../../../graphql/user/queries/index"
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface ListComment {
  listComment: { data: { adSellLeaseComments: { edges: ListCommentProps[]}} },
}

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface OverallReviewsProps {
  className?:string,
  listComment: ListCommentProps[],
  userId: string
} 

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const { className } = props;
  const classes = useStyles();
  const [page, setPage] = React.useState<number>(2)
  const [moreComment, setMoveComment] = React.useState<ListComment[]>([])
  const [listMore, setListMore] = React.useState<ListComment[]>([])
  const disabledButton = false
  const setViewPage = () => {
    let filterObject =  { id: props.userId }
    let dataFilter = JSON.stringify(filterObject);
    apolloClient.query({query: GET_AD_SELL_LEASE_COMMENTS, variables: {
        "page": page,
        "limit": 2,
        "filter": dataFilter
      }}).then(response => {
        setMoveComment(response.data.adSellLeaseComments.edges)
    });
    setPage(page + 1)
    if(moreComment.length){
      moreComment.map((item: any) => {
        setListMore(listMore.concat(moreComment))
      })
    } else {
      disabledButton = true
    }
  }

  console.log("listMore", listMore)
  return (
    <div  className={clsx(classes.root, className)}>
      {props.listComment.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.node.user.avatar} >
                      {item.node.user.first_name}  {item.node.user.last_name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                      </Typography>
                      <Typography variant="body2">
                        {moment(Number(item.node.created_at)).fromNow()}
                      </Typography>
                      {/* <Typography variant="body2">
                        Dự án :  
                      </Typography> */}
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.node.content}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}

      {listMore.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.node.user.avatar} >
                      {item.node.user.first_name}  {item.node.user.last_name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.node.user.first_name} {item.node.user.last_name} </b> 
                      </Typography>
                      <Typography variant="body2">
                        {moment(Number(item.node.created_at)).fromNow()}
                      </Typography>
                      {/* <Typography variant="body2">
                        Dự án :  
                      </Typography> */}
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.node.content}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}

      <Grid container spacing={4}>
        <Grid item>
          <Button onClick={setViewPage} >Hiển thị thêm</Button>
        </Grid>
      </Grid>
    </div>
  );
};


export default Project;
