import React, { useEffect, useState, FunctionComponent } from "react";
import {
  Button,
  Typography,
  Popover,
  ListItem,
  ListItemText,
  ListItemIcon,
  Grow,
  Paper,
  Popper,
  MenuItem,
  MenuList,
  Hidden
} from "@material-ui/core";
import Link from "next/link";
import ClickAwayListener from "@material-ui/core/ClickAwayListener";
import Validator from "../../modules/validator";
import SearchIcon from "@material-ui/icons/Search";
import { localStorageSearch } from "../../constants";
import { createMuiTheme, ThemeProvider } from "@material-ui/core/styles";
import { apolloClient } from "../../utils";
import { GET_PROJECT_LIST_NAME } from "../../graphql/project/queries";
import { GET_AD_SELL_LEASES } from "../../graphql/ad_sell_leases/queries";
import { makeStyles, createStyles } from "@material-ui/core/styles";
import EcoIcon from "@material-ui/icons/Eco";
import ViewList from "@material-ui/icons/ViewList";
import {InputBase} from "@material-ui/core";
import Router from "next/router";

const useStyles = makeStyles(theme =>
  createStyles({
    root: {
      display: "flex"
    },
    placementHeaderSearch: {
      width: "100%",
      [theme.breakpoints.up("sm")]: {
        marginLeft: "-40px"
      },
      [theme.breakpoints.down("xs")]: {}
    },
    placementHomeSearch: {
      width: "100%",
      paddingTop: "12px",
      zIndex: 1000,
      [theme.breakpoints.up("lg")]: {
        width: "calc(100% - 40px)",
        marginLeft: "-200px"
      },
      [theme.breakpoints.up("md")]: {
        width: "calc(100% - 40px)",
        marginLeft: "-200px"
      },
      [theme.breakpoints.up("sm")]: {
        width: "calc(100% - 40px)",
        marginLeft: "-200px"
      },
      [theme.breakpoints.down("sm")]: {
        width: "calc(100% - 40px)",
        paddingTop: "7px"
        // marginLeft: "4px"
      }
    },
    paper: {
      marginRight: theme.spacing(2)
    },
    fontMenuItem: {
      fontWeight: 900,
      fontSize: "15px"
    },
    positionDelete: {
      position: "absolute",
      right: "10px",
      color: "red",
      textAlign: "right",
      fontSize: "14px"
    },
    positionSearchHome: {}
  })
);

const theme = createMuiTheme({
  overrides: {
    MuiMenuItem: {
      root: {
        lineHeight: 1,
        fontSize: "14px",
        minHeight: "30px"
      }
    }
  }
});

const projectHot = [
  { title: "Vinhomes Central Park", type: "/du-an-bat-dong-san" },
  { title: "Vinhomes Grand Park", type: "/du-an-bat-dong-san" },
  { title: "Vinhomes Smart City", type: "/du-an-bat-dong-san" },
  { title: "Nhà Đất Hải Phòng", type: "/nha-dat-ban" },
  { title: "Nhà Đất Hà Nội", type: "/nha-dat-ban" }
];

interface ProjectSearchServer {
  edges: {
    node: {
      name: string;
      id: string;
    };
  }[];
}

interface AdSellLeasesSearchServer {
  edges: {
    node: {
      title: string;
    };
  }[];
}

interface Props {
  path: string;
  searchLocation: string;
}

let historySearch = new Array();
const MenuListComposition: FunctionComponent<Props> = (props: Props) => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [keywordSearch, setKeywordSearch] = useState("");
  const anchorRef = React.useRef(null);
  const [projectSearch, setProjectSearch] = useState<ProjectSearchServer>();

  const [adSellLeasesSearch, setAdSellLeasesSearch] = useState<AdSellLeasesSearchServer>();
  const [anchorElSearchType, setAnchorElSearchType] = React.useState<HTMLButtonElement | null>(null);
  const AD = "tin-rao";
  const AD_HOUSING = "nha-dat-ban";
  const PROJECT_HOUSING = "du-an-bat-dong-san";
  const PROJECT = "du-an";

  const dropdownSearch = [
    { title: "Dự án", type: PROJECT_HOUSING },
    { title: "Tin rao", type: AD_HOUSING }
  ];

  let pathProps = props.path;
  let defaultDropdown = dropdownSearch[0];
  if (pathProps === AD || pathProps === AD_HOUSING) {
    defaultDropdown = dropdownSearch[1];
  }
  const [typeSearch, setTypeSearch] = React.useState(defaultDropdown);
  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  };

  function handleListKeyDown(event: any) {
    if (event.key === "Tab") {
      event.preventDefault();
      setOpen(false);
    }
  }

  const removeLocalSearch = () => {
    localStorage.removeItem(localStorageSearch);
    setOpen(false);
    setKeywordSearch("");
  };

  const _id = open ? "simple-popover" : undefined;
  const prevOpen = React.useRef(open);
  useEffect(() => {
    historySearch = JSON.parse(localStorage.getItem(localStorageSearch) as string);
    if (!Array.isArray(historySearch)) historySearch = [];
    if (!Validator.isBlank(localStorage.getItem(localStorageSearch))) {
      localStorage.setItem(localStorageSearch, JSON.stringify(historySearch));
    }
    if (prevOpen.current === true && open === false) {
      // anchorRef.current.focus();
    }
    prevOpen.current = open;
  }, [open]);

  const onBlur = (e: any) => {
    if (e.target.value) {
      saveLocalStorege(e);
    }
  };

  const handleKeyDownSearch = (e: any) => {
    if (e.key == "Enter" || e.keyCode == 13) {
      saveLocalStorege(e);
    }
  };

  const saveLocalStorege = (e: any) => {
    if (!Array.isArray(historySearch)) historySearch = [];
    let checkExist = false;
    historySearch.map(item => {
      if (item && item.title === e.target.value) {
        checkExist = true;
      }
    });
    if (!checkExist || historySearch.length == 0) {
      historySearch.unshift({ title: e.target.value, type: `/${typeSearch.type}` });
    }
    if (historySearch.length >= 4) {
      historySearch.splice(3, 1);
    }
    console.log("Local Storage", historySearch);
    localStorage.setItem(localStorageSearch, JSON.stringify(historySearch));
  };

  const onClickSearch = (title: string, type: string) => {
    setKeywordSearch("");
    setOpen(false);
    let urlProcess = { pathname: `${type}`, query: { name: title } };
    let slugAs = `${type}?name=` + title;
    Router.push(urlProcess, slugAs, { shallow: false });
  };

  const onChangeSearch = (e: any) => {
    if (e.key == "Enter" || e.keyCode == 13) {
      buttonSearch(0, 0);
    }

    setKeywordSearch(e.target.value);
    let filterProject = { name: e.target.value };
    let filterAdSellLeases = { title: e.target.value };
    let dataFilterProject = JSON.stringify(filterProject);
    let dataFilterAdSellLeases = JSON.stringify(filterAdSellLeases);
    let limit =
      pathProps == AD || pathProps == AD_HOUSING || pathProps == PROJECT_HOUSING || pathProps == PROJECT ? 10 : 4;
    // get tin rao
    if (pathProps == "") {
      limit = 10;
    }
    if (typeSearch.type == PROJECT || typeSearch.type == PROJECT_HOUSING) {
      // get du an
      apolloClient
        .query({
          query: GET_PROJECT_LIST_NAME,
          variables: {
            page: 1,
            limit: limit,
            filter: dataFilterProject
          }
        })
        .then(response => {
          setProjectSearch(response.data.projects.edges);
        });
    }
    if (typeSearch.type == AD || typeSearch.type == AD_HOUSING) {
      // get tin rao
      apolloClient
        .query({
          query: GET_AD_SELL_LEASES,
          variables: {
            page: 1,
            limit: limit,
            filter: dataFilterAdSellLeases
          }
        })
        .then(response => {
          setAdSellLeasesSearch(response.data.adSellLeases.edges);
        });
    }
  };

  const buttonSearch = (typeRequest: any, keySearch: any) => {
    let key = keywordSearch;
    if (typeof keySearch == "string") {
      key = keySearch;
    }

    if (typeSearch.type === AD_HOUSING) {
      let urlProcess = { pathname: "/nha-dat-ban", query: { title: key } };
      let slugAs = "/nha-dat-ban?title=" + key;
      Router.push(urlProcess, slugAs, { shallow: false });
    } else {
      let urlProcess = { pathname: "/du-an-bat-dong-san", query: { name: key } };
      let slugAs = "/du-an-bat-dong-san?name=" + key;
      Router.push(urlProcess, slugAs, { shallow: false });
    }
  };

  const handleClickSearchType = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorElSearchType(event.currentTarget);
  };

  const handleChangeType = (inputType: { title: string; type: string }) => {
    setTypeSearch(inputType);
    handleCloseSearchType();
  };

  const handleCloseSearchType = () => {
    setAnchorElSearchType(null);
  };

  const onChangeWards = (event: any) => {
    setKeywordSearch(event.target.value);
    if (event.key == "Enter" || event.keyCode == 13) {
      buttonSearch(0, 0);
    }
  };

  const openSearchType = Boolean(anchorElSearchType);
  let converProject = new Array();
  let convetAdSellLeases = new Array();
  if (Array.isArray(projectSearch)) {
    projectSearch.map((item: any) => {
      converProject.push({ title: item.node.name, type: "/du-an-bat-dong-san" });
    });
  }
  if (Array.isArray(adSellLeasesSearch)) {
    adSellLeasesSearch.map((item: any) => {
      convetAdSellLeases.push({ title: item.node.title, type: "/nha-dat-ban" });
    });
  }

  const renderNewSearch =
    typeSearch.type == AD || typeSearch.type == AD_HOUSING ? (
      <>
        <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
          {
            <MenuItem key="tinrao">
              <Typography className={classes.fontMenuItem}>TIN RAO</Typography>
            </MenuItem>
          }
          {convetAdSellLeases.map((item: any, index: number) => (
            <MenuItem key={index} onClick={() => onClickSearch(item.title, item.type)}>
              {item.title}
            </MenuItem>
          ))}
        </MenuList>
      </>
    ) : typeSearch.type == PROJECT || typeSearch.type == PROJECT_HOUSING ? (
      <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
        {
          <MenuItem>
            <Typography className={classes.fontMenuItem}>DỰ ÁN</Typography>
          </MenuItem>
        }
        {converProject.map((item: any, index: number) => (
          <MenuItem key={index} onClick={() => onClickSearch(item.title, item.type)}>
            {item.title}
          </MenuItem>
        ))}
      </MenuList>
    ) : (
      <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
        {
          <MenuItem>
            <Typography className={classes.fontMenuItem}>TIN RAO</Typography>
          </MenuItem>
        }
        {convetAdSellLeases.map((item: any, index: number) => (
          <MenuItem key={index} onClick={() => onClickSearch(item.title, item.type)}>
            {item.title}
          </MenuItem>
        ))}
        {
          <MenuItem>
            <Typography className={classes.fontMenuItem}>DỰ ÁN</Typography>
          </MenuItem>
        }
        {converProject.map((item: any, index: number) => (
          <MenuItem key={index} onClick={() => onClickSearch(item.title, item.type)}>
            {item.title}
          </MenuItem>
        ))}
      </MenuList>
    );

  const renderHot = projectHot.map((item: any, index: number) => (
    <MenuItem key={index} onClick={() => onClickSearch(item.title, item.type)}>
      <Link href={`${item.type}`}>
        <a style={{ color: "black" }}>{item.title}</a>
      </Link>
    </MenuItem>
  ));

  const renderHistory = historySearch.map((item: any, index: number) => (
    <MenuItem key={index + 10} onClick={() => onClickSearch(item.title, item.type)}>
      {item.title}
    </MenuItem>
  ));

  const renderSearchHeader = (
    <>
      <div className="header__center__search__icon">
        <svg viewBox="0 0 512 512">
          <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z" />
        </svg>
      </div>
      <div className="header__center__search__box">
        <InputBase
          fullWidth={true}
          placeholder="Tìm kiếm ... "
          defaultValue={keywordSearch}
          onKeyDown={handleKeyDownSearch}
          autoComplete="off"
          ref={anchorRef}
          onChange={(e: any) => onChangeSearch(e)}
          onBlur={(e: any) => onBlur(e)}
          onClick={handleToggle}
          onKeyUp={e => onChangeWards(e)}
        />
      </div>
      <div className="header__center__search__type">
        <Button onClick={handleClickSearchType} aria-describedby={_id}>
          <svg viewBox="0 0 320 512">
            <path d="M143 352.3L7 216.3c-9.4-9.4-9.4-24.6 0-33.9l22.6-22.6c9.4-9.4 24.6-9.4 33.9 0l96.4 96.4 96.4-96.4c9.4-9.4 24.6-9.4 33.9 0l22.6 22.6c9.4 9.4 9.4 24.6 0 33.9l-136 136c-9.2 9.4-24.4 9.4-33.8 0z" />
          </svg>
          {typeSearch.title}
        </Button>
        <Popover
          id={_id}
          open={openSearchType}
          anchorEl={anchorElSearchType}
          onClose={handleCloseSearchType}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
          className="header__center__search__type__popup"
        >
          <Typography component={"span"}>
            <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[0])}>
              <ListItemIcon>
                <EcoIcon />
              </ListItemIcon>
              <ListItemText primary={dropdownSearch[0].title} />
            </ListItem>
            <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[1])}>
              <ListItemIcon>
                <ViewList />
              </ListItemIcon>
              <ListItemText primary={dropdownSearch[1].title} />
            </ListItem>
          </Typography>
        </Popover>
      </div>
      <Button variant="outlined" onClick={() => buttonSearch(0, 0)} className="header__center__search__button">
        <svg viewBox="0 0 512 512">
          <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z" />
        </svg>
      </Button>
    </>
  );
  const renderSearchHome = (
    <>
      <Hidden xsDown>
        <div className="search_homepage__body__box__select">
          <Button onClick={handleClickSearchType} aria-describedby={_id}>
            {typeSearch.title}
            <svg viewBox="0 0 320 512">
              <path d="M143 352.3L7 216.3c-9.4-9.4-9.4-24.6 0-33.9l22.6-22.6c9.4-9.4 24.6-9.4 33.9 0l96.4 96.4 96.4-96.4c9.4-9.4 24.6-9.4 33.9 0l22.6 22.6c9.4 9.4 9.4 24.6 0 33.9l-136 136c-9.2 9.4-24.4 9.4-33.8 0z" />
            </svg>
          </Button>
          <Popover
            id={_id}
            open={openSearchType}
            anchorEl={anchorElSearchType}
            onClose={handleCloseSearchType}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "center"
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "center"
            }}
            className="search_homepage__body__box__select__popup"
          >
            <Typography component={"span"}>
              <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[0])}>
                <ListItemIcon>
                  <EcoIcon />
                </ListItemIcon>
                <ListItemText primary={dropdownSearch[0].title} />
              </ListItem>
              <ListItem button onClick={(event: React.MouseEvent<HTMLElement>) => handleChangeType(dropdownSearch[1])}>
                <ListItemIcon>
                  <ViewList />
                </ListItemIcon>
                <ListItemText primary={dropdownSearch[1].title} />
              </ListItem>
            </Typography>
          </Popover>
        </div>
      </Hidden>
      <div className="search_homepage__body_box_input">
        <InputBase
          fullWidth={true}
          placeholder="Nhập thông tin tìm kiếm ... "
          className="search_homepage__body_box_input_base"
          defaultValue={keywordSearch}
          autoComplete="off"
          ref={anchorRef}
          onChange={(e: any) => onChangeSearch(e)}
          onBlur={(e: any) => onBlur(e)}
          onClick={handleToggle}
          onKeyUp={e => onChangeWards(e)}
        />
      </div>
      <Button variant="outlined" onClick={() => buttonSearch(0, 0)} className="search_homepage__body_box_button">
        <SearchIcon />
      </Button>
    </>
  );
  const renderHotHistory = (
    <MenuList id="menu-list-grow" onKeyDown={handleListKeyDown}>
      <MenuItem key="tukhoahot">
        {" "}
        <Typography className={classes.fontMenuItem}>TỪ KHÓA HOT</Typography>
      </MenuItem>
      {renderHot}
      <MenuItem key="lichsutimkiem">
        <Typography className={classes.fontMenuItem}>LỊCH SỬ TÌM KIẾM</Typography>
        <Typography className={classes.positionDelete} onClick={removeLocalSearch}>
          Xóa
        </Typography>
      </MenuItem>
      {renderHistory}
    </MenuList>
  );

  return (
    <div className={classes.root}>
      <ThemeProvider theme={theme}>
        <div style={{ minHeight: "30px" }}>
          {props.searchLocation == "header" ? renderSearchHeader : renderSearchHome}
        </div>
        <Popper
          className={props.searchLocation == "header" ? classes.placementHeaderSearch : classes.placementHomeSearch}
          placement="bottom-start"
          open={open}
          anchorEl={anchorRef.current}
          role={undefined}
          transition
          disablePortal
        >
          {({ TransitionProps, placement }) => (
            <Grow
              {...TransitionProps}
              style={{ transformOrigin: placement === "bottom" ? "left top" : "center bottom" }}
            >
              <Paper>
                <ClickAwayListener onClickAway={() => setOpen(false)}>
                  {keywordSearch ? renderNewSearch : renderHotHistory}
                </ClickAwayListener>
              </Paper>
            </Grow>
          )}
        </Popper>
      </ThemeProvider>
    </div>
  );
};

export default MenuListComposition;
