import React, { FunctionComponent } from "react";
import Members from "./Tabs/Members";
import Rating from "./Tabs/Rating";
import Introduce from "./Tabs/Introduce";
import AdSellLeases from "./Tabs/AdSellLeases";
import Header from "./Header";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Paper from "@material-ui/core/Paper";
import { ThemeProvider } from '@material-ui/core/styles';
import StyledCheckbox from "./StyledCheckbox";
import _ from 'lodash'
import {
  Tabs,
  Tab,
  Divider,
  Grid,
  Button,
  Typography,
  Avatar,
  Hidden,
} from "@material-ui/core";
import { relatedChannel, indexStyles } from "./Constant";
import { createMuiTheme } from '@material-ui/core/styles'
import { Badge } from '@material-ui/core'
import { Theme, makeStyles, withStyles, createStyles } from '@material-ui/core/styles'
import { GET_CHANNEL_BY_SLUG } from "../../graphql/channel/queries"
import { apolloClient } from "../../utils";


const themeInput = createMuiTheme({
  overrides: {
    MuiTab: {
      root: {
        padding: "6px",
      },
      wrapper: {
        fontSize: "13px",
        fontWeight: 700,
      },
    },
  },
});

interface IndexProps {
  channel: ChannelProp
  userLogin: string
  slug: string
}

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
}

interface CommentsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    like_number: string;
    created_at: string;
  };
}

interface UserProps {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  intro: string;
  id: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

interface RatingsProp {
  node: {
    id: string;
    ad_sell_lease_id: string;
    content: string;
    user_id: string;
    media: string;
    user: {
      first_name: string;
      last_name: string;
      avatar: string;
    };
    rate: number;
    like_number: string;
    created_at: string;
  };
}

const Index: FunctionComponent<IndexProps> = (props: any) => {
  const classes = indexStyles();
  const [itemTab, setItemTab] = React.useState("rating");
  const [count, setCount] = React.useState<number>(0);
  const [channel, setChannel] = React.useState<ChannelProp>()
  const channelId = _.get(props, "channel.id", "")
  const tabs = [
    { value: "ad", label: "Tin rao" },
    { value: "introduce", label: "Giới thiệu" },
    { value: "members", label: "Môi giới" },
    { value: "rating", label: "Đánh giá" },
  ];

  const getChannel = async () => {
    let res = await apolloClient.query({ query: GET_CHANNEL_BY_SLUG, variables: {
      "slug": props.slug,
      }}).then((response: any) => {
        let channel = _.get(response, "data.channel", {});
        setChannel(channel)
    });
  }

  React.useEffect(() => {
    getChannel()
  })
  console.log("channel", channel)

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value);
  };

  const renderTabs = () => {
    return (
      <Paper square className={classes.rootTabs} >
        <Hidden mdUp>
          <ThemeProvider theme={themeInput}>
            <Tabs
              onChange={handleTabsChange}
              scrollButtons="auto"
              indicatorColor="primary"
              textColor="primary"
              variant={"fullWidth"}
              value={itemTab}
            >
              {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
            </Tabs>
          </ThemeProvider>
        </Hidden>
        <Hidden smDown>
          <Tabs
            onChange={handleTabsChange}
            scrollButtons="auto"
            indicatorColor="primary"
            textColor="primary"
            variant={ "standard" }
            value={itemTab}
          >
            {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
          </Tabs>
        </Hidden>
      </Paper>
    )
  }

  const renderTab = () => {
    return (
      <div className={classes.contentTab}>
        {itemTab === "ad" && <AdSellLeases channelId={channelId} />}
        {itemTab === "introduce" && <Introduce channelId={channelId} />}
        {itemTab === "members" && <Members channelId={channelId} />}
        {itemTab === "rating" && <Rating channelId={channelId} />}
      </div>
    )
  }

  const renderRelatedChannel = () => {
    return (
      <div className={classes.paddingRelatedChannel}>
        <Typography variant="h6" className={classes.title}>Kênh liên </Typography>
        {relatedChannel.map((item: any, index: number) => {
          if ( index < count * 6 + 6 ) {
            return (
              <div className={classes.contentChannel} key={index} >
                <Grid container spacing={4} alignItems="center" justify="center" className={classes.padingContent}>
                  <Grid item xs={12}>
                    <div className={classes.avatarLeft}>
                      <Avatar src={item.image} className={classes.large} alt="" />
                    </div>
                    <div className={classes.contentFollow} >
                      <Typography className={classes.titleFollow}>
                        {item.title}
                      </Typography>
                      <Typography className={classes.follow}>
                        {item.numberfollow}Lượt theo dõi 
                      </Typography>
                      {item.follow && <CheckCircleIcon style={{ color: "#045EA8", fontSize: "15px", float: "right" }} />}
                    </div>
                    <div className={classes.clearFloat}></div>
                  </Grid>
                </Grid>
              </div>
            );
          }
          return ""
        })}
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Button onClick={() => setCount(count + 1)} fullWidth className={classes.button}>
              Xem thêm
            </Button>
          </Grid>
        </Grid>
      </div>
    )
  }

  return (
    <div>
      <Header userLogin={props.userLogin} channel={propschannel} />
      <div className={classes.paddingIndex}>
        {renderTabs()}
        <Grid container spacing={0} className={classes.paddingContentTab}>
          <Grid item xs={12} md={9}>
            {renderTab()}
          </Grid>
          <Grid item xs={12} md={3}>
            {renderRelatedChannel()}
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Index;
