import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { postsInfo } from "./Constants";
import { makeStyles } from "@material-ui/core/styles"

const indexStyles = makeStyles((theme) => ({
  padingContent: {
    paddingRight : "28px",
    [theme.breakpoints.down("xs")]: {
      padding: "0px",
    }
  },
  image: {
    paddingRight : "28px",
    [theme.breakpoints.down("xs")]: {
      padding: "0px",
    }
  },
  introduceTitle: {
    padding: "20px 0px",
    fontWeight: 700,
    [theme.breakpoints.down("sm")]: {
      padding: "8px 8px",
      fontWeight: 700,
      textAlign: "left",
    },
    [theme.breakpoints.down("xs")]: {
      padding: "0px 4px",
      fontWeight: 700,
      textAlign: "left",
    },
  },
  introduceContent: {
    textAlign: "justify",
  },
  introduceContentDetail: {
    textAlign: "justify",
    textIndent: "24px",
    [theme.breakpoints.down("xs")]: {
      textIndent: "16px",
    },
  }
}));

const Mission = () => {
  const classes = indexStyles();

  const renderContent = (element: any) => {
    return (
      <Grid item md={6} >
        <div className={classes.padingContent}>
          <Typography variant="h5" className={classes.introduceTitle} >
            {element.title}
          </Typography>
          {element.contents.content.map((e: any, count: number) => 
            <Typography key={count} className={classes.introduceContent}>{e.item}</Typography>
          )}
          {element.contents.contentDetail.map((e: any, count: number) => 
            <Typography key={count} className={classes.introduceContentDetail}>- {e.item}</Typography>
          )}
        </div>
      </Grid>
    )
  };
  
  const renderImage = (element: any) => {
    return (
      <Grid item md={6}>
        <div className={classes.image}>
          <img src={element.image} />
        </div>
      </Grid>
    )
  };

  return (
    <div className={"mission-area body-introduce"}>
      {postsInfo.map((element: any, index: number) => {
        return (
          <div>
            { element.type === 1 
              ? <Grid container direction="row" spacing={1} alignItems={"center"}>
                  {renderContent(element)}
                  {renderImage(element)}
                </Grid>
              : <Grid container direction="row" spacing={1} alignItems={"center"}>
                  <Hidden smDown>
                    <>
                      {renderImage(element)}
                      {renderContent(element)}
                    </>
                  </Hidden>
                  <Hidden mdUp>
                    <>
                      {renderContent(element)}
                      {renderImage(element)}
                    </>
                  </Hidden>
                </Grid>
            }
          </div>
        )
      })}
    </div>
  );
};

export default Mission
