import React from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import {apolloClient} from "../../utils";
import { GET_CHANNEL_BY_SLUG } from "../../graphql/channel/queries";
import "../../assets/scss/style.scss";
import { mainURL } from "../../constants";
import Channel from "../../components/Channel/Index"
import _ from "lodash"
interface IndexProps {
  // channel: { data: { channel: ChannelProp}}
  slug: string
}

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
}

interface QueryProp { 
  query: { slug: string } 
}

// @ts-ignore
const UserPage: NextPage<IndexProps> = (props: IndexProps) => {

  let metaTitle = props.channel.data.channel.name
  let imgBanner = "https://static.nhadatmoi.net/project/1585911111698_chung-cu-b2-truong-sa-phuong-17-quan-binh-thanh-1.jpg"
  let userLogin = _.get(props, 'user._id', "")

  const getChannel = async () => {
    let channel = await apolloClient.query({ query: GET_CHANNEL_BY_SLUG, variables: {
        "slug": props.slug,
      }}).then((response: any) => {
      return response
    });
  }

  React.useEffect(() => {
    getChannel()
  })

  return (
    <Layout
      title={metaTitle}
      description={""}
      {...props}
      image={imgBanner}
      canonical={ mainURL + '/channel/' + props.slug}
      path={'channel'}
    >
      <Channel slug={props.slug} channel={props.channel.data.channel} userLogin={userLogin} />
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  let slugChanel = props.query.slug;
  let channel = await apolloClient.query({ query: GET_CHANNEL_BY_SLUG, variables: {
      "slug": slugChanel,
    }}).then((response: any) => {
    return response
  });
  
  return {
    channel: channel,
    slug: slugChanel
  };
};


export default UserPage;
