import React from 'react';
import clsx from 'clsx';
import { Grid, Button } from '@material-ui/core'
import {makeStyles} from '@material-ui/core/styles'
import GridAdSellLeases from '../../GridList/GridAdSellLeases'
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASES} from "../../graphql/ad_sell_leases/queries"


const useStyles = makeStyles(theme => ({
    root: {},
    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    }
}));

interface AdProps  {
  node: {
      title: string,
      slug: string,
      created_at: string,
      updated_at: string,
      price: {
        unit: string
        value: string
      },
      avatar: {
        alt_text: string
        src: string
        description: string
      },
      address: {
        text: string,
        ward: string,
        district: string,
        city: string
      },
      contact: {
        image: string,
        name: string,
        address: string,
        mobile: string,
        phone: string,
        email: string,
      }
      area: {
        unit: string
        value: string
      }
  },
}


interface ListAdProps {
    className ?: string,
    list_ad: AdProps[],
    userId: String
}

interface AdProps  {
    node: {
        title: string,
        slug: string,
        created_at: string,
        updated_at: string,
        price: {
          unit: string
          value: string
        },
        avatar: {
          alt_text: string
          src: string
          description: string
        },
        address: {
          text: string,
          ward: string,
          district: string,
          city: string
        },
        contact: {
          image: string,
          name: string,
          address: string,
          mobile: string,
          phone: string,
          email: string,
        }
        area: {
          unit: string
          value: string
        }
    },
}

const Ad: React.FunctionComponent<ListAdProps> = (props: ListAdProps) => {
    const { className, ...rest } = props;
    const classes = useStyles();
    const [list_ad, setListAd] = React.useState<AdProps[]>([]);
    React.useEffect(() => {
      let dataFilterAd = { 
        user: props.userId,
      }
      let filterUser = JSON.stringify(dataFilterAd)
      apolloClient.query({query: GET_AD_SELL_LEASES, variables: {
        "page": 1,
        "limit": 12,
        "filter": filterUser 
      }}).then(response => { 
        setListAd(response.data.adSellLeases.edges)
      })
    })

    return (
      <div {...rest} className={clsx(classes.root, className)}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            {Array.isArray(props.list_ad) &&
            <GridAdSellLeases 
              grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} 
              gridData={props.list_ad} 
              typeView={"grid"}
              className="grid__list"
            />}
          </Grid>
          <Grid item xs={12} className="pagination__block">
            {
              <Button className="pagination__block__prev"  >
                <svg viewBox="0 0 448 512">
                  <path d="M152.485 396.284l19.626-19.626c4.753-4.753 4.675-12.484-.173-17.14L91.22 282H436c6.627 0 12-5.373 12-12v-28c0-6.627-5.373-12-12-12H91.22l80.717-77.518c4.849-4.656 4.927-12.387.173-17.14l-19.626-19.626c-4.686-4.686-12.284-4.686-16.971 0L3.716 247.515c-4.686 4.686-4.686 12.284 0 16.971l131.799 131.799c4.686 4.685 12.284 4.685 16.97-.001z"/>
                </svg> 
                Trang trước
              </Button>
            }
            {
              <Button className="pagination__block__next"  >
                Trang sau 
                <svg viewBox="0 0 448 512">
                  <path d="M295.515 115.716l-19.626 19.626c-4.753 4.753-4.675 12.484.173 17.14L356.78 230H12c-6.627 0-12 5.373-12 12v28c0 6.627 5.373 12 12 12h344.78l-80.717 77.518c-4.849 4.656-4.927 12.387-.173 17.14l19.626 19.626c4.686 4.686 12.284 4.686 16.971 0l131.799-131.799c4.686-4.686 4.686-12.284 0-16.971L312.485 115.716c-4.686-4.686-12.284-4.686-16.97 0z"/>
                </svg>
              </Button>
            }
          </Grid>
        </Grid>
      </div>
    );
};


export default Ad;
