import React from "react";
import _ from "lodash";
import { Grid, Button, Typography, Hidden } from "@material-ui/core";
import { qualityTitle, extraQualityTitle, qualitys } from "./Constants"
import { makeStyles } from "@material-ui/core/styles"

const benefitStyles = makeStyles((theme) => ({
  benefit: {
    textAlign: "center"
  },
  titleFontWeight: {
    fontWeight: 700
  },
  titlEextraFontWeight: {
    fontWeight: 500
  }
}));
const Benefit = () => {
  const classes = benefitStyles();
  return (
    <div className={"mission-area body-introduce"} >
      <Grid container alignItems="flex-start" direction="row" justify="center" spacing={10} className={classes.benefit}>
        <Grid item xs={12}>
          <Typography variant="h4" className={classes.titleFontWeight}>
            {qualityTitle}
          </Typography>
          <Typography className={classes.titlEextraFontWeight}>
            {extraQualityTitle}
          </Typography>
        </Grid>
      </Grid>
      <Grid container alignItems="flex-start" direction="row" justify="center" spacing={10}>
        {qualitys.map((element: any, index: number) => {
          return (
            <Grid item xs={4}>
              <div style={{ textAlign: 'center' }} >
                <img src={element.icon} style={{ width: 100, height: 120, padding: "12px" }} />
              </div>
              <Typography variant="h6" style={{ fontWeight: 500, textAlign: 'center'}}>
                {element.title}
              </Typography>
              <Typography variant="h6" style={{ verticalAlign: "middle", textAlign: "justify" }} >
                {element.content.map((e: any, count: number) => {
                  if(count === 0){
                    return <Typography>{e.item}</Typography>
                  } else {
                    return <Typography><li>{e.item}</li></Typography> 
                  }
                })}
              </Typography>
            </Grid>
          )
        })}
      </Grid>
     
    </div>    
  );
};

export default Benefit;
