import React, { FunctionComponent } from "react";
import Members from "./Tabs/Members";
import Rating from "./Tabs/Rating";
import Introduce from "./Tabs/Introduce";
import AdSellLeases from "./Tabs/AdSellLeases";
import Header from "./Header";
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import Paper from "@material-ui/core/Paper";
import { ThemeProvider } from '@material-ui/core/styles';
import _ from 'lodash';
import { Tabs, Tab, Grid, Button, Typography, Avatar, Hidden } from "@material-ui/core";
import { relatedChannel, indexStyles } from "./Constant";
import { createMuiTheme } from '@material-ui/core/styles';
import { GET_CHANNELS } from "../../graphql/channel/queries";
import { apolloClient } from "../../utils";
import LoadingCircle from "./LoadingCircle"

const themeInput = createMuiTheme({
  overrides: {
    MuiTab: {
      root: {
        padding: "6px",
      },
      wrapper: {
        fontSize: "13px",
        fontWeight: 700,
      },
    },
  },
});

interface IndexProps {
  userLogin: string
  channel: ChannelProp
  slug: string
};

interface Channels {
  edges: {
    node: {
      id: string
      name: string
      slug: string
      avatar: string 
      cover: string  
      official_status: number 
      follow_value: number
    }
  }
};

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
};


const tabs = [
  { value: "ad", label: "Tin rao" },
  { value: "introduce", label: "Giới thiệu" },
  { value: "members", label: "Môi giới" },
  { value: "rating", label: "Đánh giá" },
];

const Index: FunctionComponent<IndexProps> = (props: any) => {
  const classes = indexStyles();
  const [itemTab, setItemTab] = React.useState("members");
  const [count, setCount] = React.useState<number>(0);
  const [channels, setChannels] = React.useState<Channels[]>([]);
  const [loading, setLoading] = React.useState(true);
  const channelId = _.get(props, "channel.id", "");
  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value);
  };

  React.useEffect(()=> {
    getChannels(1)
  })

  const getChannels = async (page: number) => { 
    let channels = await apolloClient.query({ query: GET_CHANNELS, variables: {
      "limit": 8,
      "page": 1,
      "filter": "{}",
    }})
    .then((response: any) => {
      let edges = _.get(response, "data.channels.edges", [])
      setChannels(edges)
      setLoading(false)
    });
  };


  const renderTabs = () => {
    return (
      <Paper square className={classes.rootTabs} >
        <Hidden mdUp>
          <ThemeProvider theme={themeInput}>
            <Tabs
              onChange={handleTabsChange}
              scrollButtons="auto"
              indicatorColor="primary"
              textColor="primary"
              variant={"fullWidth"}
              value={itemTab}
            >
              {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
            </Tabs>
          </ThemeProvider>
        </Hidden>
        <Hidden smDown>
          <Tabs
            onChange={handleTabsChange}
            scrollButtons="auto"
            indicatorColor="primary"
            textColor="primary"
            variant={ "standard" }
            value={itemTab}
          >
            {tabs.map((tab) => <Tab key={tab.value} label={tab.label} value={tab.value}/> )}
          </Tabs>
        </Hidden>
      </Paper>
    );
  };

  const renderTab = () => {
    return (
      <div className={classes.contentTab}>
        {itemTab === "ad" && <AdSellLeases channelId={channelId} />}
        {itemTab === "introduce" && <Introduce channelId={channelId} slug={props.slug}/>}
        {itemTab === "members" && <Members channelId={channelId}  />}
        {itemTab === "rating" && <Rating channelId={channelId} />}
      </div>
    );
  };
  
  console.log("channels", channels)
  const viewMore = (count) => {
    setCount(count)
    getChannels(count)

  }

  const _renderRelatedChannel = () => {
    return (
      <div className={classes.paddingRelatedChannel}>
        <Typography variant="h6" className={classes.title}>Kênh liên quan </Typography>
        {loading ? <LoadingCircle /> : ""}
        {channels.map((element: any, index: number) => {
          console.log('element.node.name',element.node.name[0])
          return (
            <div className={classes.contentChannel} key={index} >
              <Grid container spacing={4} alignItems="center" justify="center" className={classes.paddingContent}>
                <Grid item xs={12}>
                  <div className={classes.avatarLeft}>
                    {element.node.avatar 
                    ? <Avatar src={element.node.avatar} className={classes.large} alt="" />
                    : <Avatar src={element.node.avatar} className={classes.large} alt="">{_.get(element, "node.name[0]", "") }</Avatar>}
                  </div>
                  <div className={classes.contentFollow} >
                    <Typography className={classes.titleFollow}>
                      {element.node.name}
                    </Typography>
                    <Typography className={classes.follow}>
                      {element.node.follow_value} Lượt theo dõi 
                    </Typography>
                    {element.node.official_status ? <CheckCircleIcon style={{ color: "#045EA8", fontSize: "15px", float: "right" }} /> : ""}
                  </div>
                  <div className={classes.clearFloat}></div>
                </Grid>
              </Grid>
            </div>
          )
        })}
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Button onClick={() => viewMore(count + 1)} fullWidth className={classes.button}>
              Xem thêm
            </Button>
          </Grid>
        </Grid>
      </div>
    )
  }

  const renderRelatedChannel = () => {
    return (
      <div className={classes.paddingRelatedChannel}>
        <Typography variant="h6" className={classes.title}>Kênh liên quan </Typography>
        {LoadingCircle ? "" : ""}
        {relatedChannel.map((item: any, index: number) => {
          if ( index < count * 6 + 6 ) {
            return (
              <div className={classes.contentChannel} key={index} >
                <Grid container spacing={4} alignItems="center" justify="center" className={classes.paddingContent}>
                  <Grid item xs={12}>
                    <div className={classes.avatarLeft}>
                      <Avatar src={item.image} className={classes.large} alt="" />
                    </div>
                    <div className={classes.contentFollow} >
                      <Typography className={classes.titleFollow}>
                        {item.title}
                      </Typography>
                      <Typography className={classes.follow}>
                        {item.numberFollow}Lượt theo dõi 
                      </Typography>
                      {item.follow && <CheckCircleIcon style={{ color: "#045EA8", fontSize: "15px", float: "right" }} />}
                    </div>
                    <div className={classes.clearFloat}></div>
                  </Grid>
                </Grid>
              </div>
            );
          }
          return ""
        })}
        <Grid container spacing={4}>
          <Grid item xs={12}>
            <Button onClick={() => setCount(count + 1)} fullWidth className={classes.button}>
              Xem thêm
            </Button>
          </Grid>
        </Grid>
      </div>
    );
  };

  
  return (
    <div>
      <Header userLogin={props.userLogin} channel={props.channel} slug={props.slug}/>
      <div className={classes.paddingIndex}>
        {renderTabs()}
        <Grid container spacing={0} className={classes.paddingContentTab}>
          <Grid item xs={12} md={9}>
            {renderTab()}
          </Grid>
          <Grid item xs={12} md={3}>
            {_renderRelatedChannel()}
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Index;
