import gql from "graphql-tag";
const SEN_MESSAGE = gql`
  mutation update($input: ChatMessageInput!) {
    chatMessage (input: $input) {
      message {
        text
        attachment {
          type
          payload {
            url
          }
        }
      }
    }{
      id
      conversation {
        message {
          text
        }
      }
    }
  }
`;

const COUNT_VIEW_AD_SELL_LEASE = gql`
  mutation adSellLeaseViewUpdate ($input: UpdateAdSellLeaseViewInput!) {
    adSellLeaseViewUpdate (input: $input) {
      id
    }
  }
`;

export {
  SEN_MESSAGE
}
