import * as React from 'react';
import {useEffect} from "react";
//Import Components
import Layout from '../components/Layout/Layout';
import SearchHomepage from "../components/Index/SearchHomepage";
import TitleBlock from "../components/Index/TitleBlock";
import GridAdSellLeases from "../components/GridList/GridAdSellLeases";
import GridProject from "../components/GridList/GridProject";
import { NextPage } from 'next';
import SearchKeyword from "../components/Search/KeywordSlide";
import BannerEcommerce from "../components/Index/BannerEcommerce";
import HotLocation from "../components/Index/HotLocation";
import LoadingPage from "../components/Display/LoadingPage";
import NewsIndex from "../components/Index/NewsIndex";
import Button from '@material-ui/core/Button';
import Link from 'next/link';
import {mainURL} from "../constants";
import fetch from 'isomorphic-unfetch';
import "../assets/scss/style.scss";
import {apolloClient} from "../utils";
import {GET_AD_SELL_LEASES, HOME_GET_AD_SELL_LEASES} from "../graphql/ad_sell_leases/queries";
import {GET_PROJECT_LIST, HOME_GET_PROJECT_LIST} from "../graphql/project/queries";
import Chat from "../components/Chat/Index"
import Validator from "../modules/validator";
import "cross-fetch/polyfill";
import ApolloClient from "apollo-boost";
// import  from 'next/dynamic'
// const Chat=dynamic(import ('../components/Chat/Chat'),{ssr:false})

interface AdSellLeasesListProps {
  node: {
    title: string,
    slug: string,
    created_at: string,
    updated_at: string,
    price: {
      unit: string
      value: string
    },
    avatar: {
      alt_text: string
      src: string
      description: string
    },
    address: {
      text: string,
      ward: string,
      district: string,
      city: string
    },
    contact: {
      image: string,
      name: string,
      address: string,
      mobile: string,
      phone: string,
      email: string,
    },
    area: {
      unit: string
      value: string
    }
  }
}
interface ProjectProps {
  node: {
    price: string,
    name: string,
    land_area: string,
    slug: string,
    created_at: string,
    updated_at: string,
    address: {
      text: string,
      ward: string,
      district: string,
      city: string
    },
    cover_image: string,
    intro_content: string
  }
}
interface DataNewsProps {
  slug: string,
  link: string,
  title: {
    rendered: string
  },
  excerpt: {
    rendered: string
  },
  featured_image_url: string,
  date: string
}

const IndexPage: NextPage<any> = (prop: any) => {
  const [dataAdSellLeases, setDataAdSellLease] = React.useState<AdSellLeasesListProps[]>([]);
  const [dataProject, setDataProject] = React.useState<ProjectProps[]>([]);
  const [dataNews, setDataNews] = React.useState<DataNewsProps[]>([]);
  const [typeView, setTypeView] = React.useState("grid")
  const [statusLoadingAdSellLease, setStatusLoadingAdSellLease] = React.useState<boolean>(true);
  const [statusLoadingProject, setStatusLoadingProject] = React.useState<boolean>(true);
  const [statusLoadingNews, setStatusLoadingNews] = React.useState<boolean>(true);
  const refTest = React.useRef(null)
  let newsUrlApi = 'https://nhadatmoi.net/tin-tuc/wp-json/wp/v2/posts?per_page=8';
  useEffect(() => {
    apolloClient.query({
      query: HOME_GET_AD_SELL_LEASES, variables: {
        "page": 1,
        "limit": 12,
        "filter": "'{}'"
      }
    }).then(response => {
      setStatusLoadingAdSellLease(false);
      if (!Validator.isBlank(response.data) && !Validator.isBlank(response.data.adSellLeases.edges)) {
        setDataAdSellLease(response.data.adSellLeases.edges);
      }
    });
    apolloClient.query({
      query: HOME_GET_PROJECT_LIST, variables: {
        "page": 1,
        "limit": 12,
        "filter": "'{}'"
      }
    }).then(response => {
      setStatusLoadingProject(false);
      if (!Validator.isBlank(response.data) && !Validator.isBlank(response.data.projects.edges)) {
        setDataProject(response.data.projects.edges);
      }
    });
    //Get Data News
    fetch(newsUrlApi)
    .then( r => r.json())
    .then( data => {
      setDataNews(data);
      setStatusLoadingNews(false);
    });
  }, []);
  const [statusModal, setModal] = React.useState(false);
  const updateModal = (data: boolean) => {
    setModal(data);
  };

  const onClickButton = () => {
    console.log("testRef", refTest.current)
  }

  let pageTitle = 'Nhà Đất Mới - Website thương mại điện tử Bất Động Sản đầu tiên tại Việt Nam';
  let pageDescription = 'Nhà Đất Mới đón đầu xu hướng bất động sản 4.0 tại Việt Nam, tích hợp trí tuệ nhân tạo để xử lý các dữ liệu, phân tích nhu cầu của cả người mua lẫn người bán.';
  return (
    <Layout
      title={pageTitle}
      description={pageDescription}
      {...prop}
      statusModal={statusModal}
      updateModal={updateModal}
      canonical={ mainURL }
      path={''}
    >
      <Chat />
      <button onClick={onClickButton}>Button</button>
      <div ref={refTest}> </div>
      {/* <SearchHomepage/>
      <div className="body_container">
        <TitleBlock title="Tin rao" description="Tổng hợp tin rao HOT nhất trên cả nước" path={"/nha-dat-ban"} type={"h2"}/>
        {
          statusLoadingAdSellLease &&
          <LoadingPage className="loading_page" grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} numberLimit={12}/>
        }
        {
          !statusLoadingAdSellLease &&
          <>
            <GridAdSellLeases 
              grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} 
              gridData={dataAdSellLeases} 
              typeView={typeView}
              className="grid__list"
            />
            <div className="content_index_button">
              <Link href="/nha-dat-ban"><Button>Xem thêm</Button></Link></div>
          </>
        }
        <BannerEcommerce {...prop} updateModal={updateModal}/>
        <TitleBlock title="Dự án" description="Tổng hợp những Dự án HOT nhất trên cả nước" path={"/du-an-bat-dong-san"} type={"h2"}/>
        {
          statusLoadingProject &&
          <LoadingPage className="loading_page" grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} numberLimit={12}/>
        }
        {
          !statusLoadingProject &&
          <>
            <GridProject 
              typeView={typeView} 
              grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} 
              gridData={dataProject} 
              className="grid__list"
            />
            <div className="content_index_button"><Link href="/du-an-bat-dong-san"><Button>Xem thêm</Button></Link></div>
          </>
        }
        <TitleBlock title="Điểm giao dịch HOT" description="Tổng hợp những địa điểm giao dịch HOT trên cả nước" type={"h2"} path={""}/>
        <HotLocation/>
        <TitleBlock title="Tin tức Tổng hợp" description="Tổng hợp tin tức liên quan tới Bất Động Sản trên cả nước" path={"https://nhadatmoi.net/tin-tuc"} type={"h2"}/>
        {
          !statusLoadingNews &&
          <>
            <NewsIndex newsData={dataNews}/>
            <div className="content_index_button"><a href={"https://nhadatmoi.net/tin-tuc"}><Button>Xem thêm</Button></a></div>
          </>
        }
        {
          statusLoadingNews &&
          <>
            <LoadingPage className="loading_page" grid={{xl: 3, lg: 3, md: 4, sm: 6, xs:12}} numberLimit={8}/>
          </> 
        }
        <SearchKeyword/>
      </div> */}
     
    </Layout>
  )
};

export default IndexPage;
