import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';
import SendIcon from '@material-ui/icons/Send';
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {
  },
  iconUp: {
    fontSize: "10",
  },
  subheader: {
    flexWrap: 'wrap',
    display: 'flex',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface ListCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}


interface OverallReviewsProps {
  className?:string,
  listComment: ListCommentProps[]
} 

interface Review {
  id: string,
  reviewer: {
    avatar: string,
    name: string,
  },
  rating: number,
  created_at: string,
  message: string
};


let reviews = [
  {
    id: "141545616541654",
    reviewer: {
      avatar: "https://zicxa.com/hinh-anh/wp-content/uploads/2019/07/T%E1%BB%95ng-h%E1%BB%A3p-h%C3%ACnh-%E1%BA%A3nh-g%C3%A1i-xinh-d%E1%BB%85-th%C6%B0%C6%A1ng-cute-nh%E1%BA%A5t-6.jpg",
      name: "Nguyễn Hồng Thúy"
    },
    rating: 3,
    created_at: "1 3 2020",
    message: "Hùng thật sự rất tuyệt vời mỗi khi gặp khách hàng",
  }, 
  {
    id: "215474854156478",
    reviewer: {
      avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg",
      name: "Mai Trâm"
    },
    rating: 4,
    created_at: "1 5 2019",
    message: "Tư vấn rất chuyên nghiệp nhiệt tình, khá thân thiện với khách hàng. Tôi đánh giá rất cao chuyên môn của bạn",
  }
]

const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const [value, setValue] = React.useState<number | null>(3);
  const [rating, setRating] = React.useState<number | null>(0)
  const [message, setMessage] = React.useState<string>("")
  const [reviewLocal, setReviewLocal] = React.useState<Review[]>([])
  const fullReviews = reviews.concat(reviewLocal)
  let couter: number = 0
  fullReviews.map((item: any) => {
    couter = item.rating + couter
  })
  const setReview = (e: any, name: string) => {
    let item = {
      id: `${new Date().getTime()}`,
      reviewer: {
        avatar: "https://caygiongnongnghiep.com/wp-content/uploads/2017/08/cay-hoa-hong-doi-mau.jpg",
        name: "Minh Quang"
      },
      rating: rating,
      created_at: moment().format("D M YYYY"),
      message: message,
    }
    if(name === "buttonSen"){
      addReview(item) 
      setMessage("")
      setRating(0)
    }
    if (name === "textField" && e.key == "Enter" || e.keyCode == 13) {
      addReview(item) 
      setMessage("")
      setRating(0)
    }
  }
  const addReview = (item: any) => setReviewLocal(state => [...reviewLocal, item])
  let ratingGeneral = couter/fullReviews.length
  const { className, ...rest } = props;
  const classes = useStyles();
  return (
    <div  className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card className={clsx(classes.root, className)}>
            <CardContent>
              <Grid alignItems="center" container spacing={3}>
                <Grid item>
                  <Typography variant="h5">Đánh giá tổng quan</Typography>
                </Grid>
                {/* <Grid className={classes.stars} item>
                  <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                    <Rating name="simple-controlled" value={ratingGeneral} />
                  </Box>
                </Grid> */}
                <Grid item>
                  <Typography color="textSecondary" variant="body2" >
                    {props.listComment.length} lượt bình luận
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {fullReviews.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.reviewer.avatar} >
                      {item.reviewer.name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      {/* <div className={classes.stars}>
                        <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                          <Rating
                            disabled={true}
                            name={`simple-controlled-${index}`}
                            value={item.rating}
                            onChange={(event, newValue) => {
                              setValue(newValue);
                            }}
                          />
                        </Box>
                      </div> */}
                      <Typography variant="body2">
                        Bình luận bởi{' '} <b>{item.reviewer.name}</b> | {moment(item.created_at).fromNow()}
                      </Typography>
                    </div>
                  }
                />
                <div className={classes.message}>
                  <Typography variant="subtitle2">{item.message}</Typography>
                </div>
              </Card>
            </Grid>
          </Grid>
        )  
      })}
      {/* <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                <Rating
                  name="simple-controlled_"
                  value={rating}
                  onChange={(event, newValue) => { setRating(newValue) }}
                />
              </Box>
              <TextField
                multiline={true}
                fullWidth={true}
                onChange={(e) => setMessage(e.target.value)}
                rowsMax={10}
                value={message}
                onKeyDown={(e) => setReview(e, "textField")}
                margin="dense"
                name="reviews"
                placeholder="Đánh giá ..."
                variant="outlined"
              />
              <Button onClick={(e) => setReview(e, "buttonSen")} color="primary" variant='contained' size="small">
                <ArrowUpwardIcon style={{fontSize: "1rem"}}/> Gửi
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
     */}
    </div>
  );
};


export default Project;
