import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles';
// import AddPost from "../../../../components/AddPost";
// import PostCard from "../../../../components/PostCard";
// import uuid from "uuid";

const useStyles = makeStyles(theme => ({
    root: {},
    posts: {
        marginTop: theme.spacing(2)
    },
    post: {
        marginBottom: theme.spacing(2)
    }
}));

interface PostType {
    id: any,
    author:{
        name:string,
        avatar:string,

    },
    created_at:string,
    message:string,
    media:string,
    avatar:string,
    name:string,
    comments:{
        id:any,
        author:{
            avatar:string,
            name:string
        }
        created_at:string,
        message:string,
    }[],
    liked:boolean,
    likes:number,
}

interface TimelineProps {
    // className?: string
}

const Timeline: React.FunctionComponent<TimelineProps> = (props: TimelineProps) => {
    // const {className, ...rest} = props;

    // const classes = useStyles();
    const [posts, setPosts] = useState<PostType[]>([]);

    useEffect(() => {
        const fetchPosts = () => {
            setPosts([{
                id: uuid(),
                author:{
                    name:"Shen Zhi",
                    avatar:"https://react-material-kit.devias.io/images/avatars/avatar_11.png",
                },
                created_at:"1 1 2020",
                message:"Just made this home screen for a project, what-cha thinkin?",
                media:"https://react-material-kit.devias.io/images/posts/post_1.jpg",
                avatar:"https://react-material-kit.devias.io/images/avatars/avatar_5.png",
                name:"string",
                comments:[{
                    id:uuid(),
                    author:{
                        avatar:"https://react-material-kit.devias.io/images/avatars/avatar_5.png",
                        name:"Anje Keizer"
                    },
                    created_at:"1 1 2020",
                    message:"Could use some more statistics, but that’s me haha",
                }],
                liked:false,
                likes:10,
            }])
        }
        fetchPosts()
    }, [])

    return (
        <div
            // {...rest}
            className={clsx(classes.root, className)}
        >
            {/* <AddPost/> */}
            {/* <div className={classes.posts}>
                {posts.map(post => (
                    <PostCard
                        className={classes.post}
                        key={post.id}
                        post={post}
                    />
                ))}
            </div> */}
            <h1>Timeline</h1>
        </div>
    );
};


export default Timeline;
