import gql from "graphql-tag";

const GET_CHANNEL_BY_SLUG = gql`
  query channel ($slug: String) {
    channel (slug: $slug) {
      id
      name
    }
  }
`;

 
const GET_MEMBER_CHANNEL_BY_ID = gql`
  query channelMember ($id: String!, $page: Int!, $limit: Int! ) {
    channelMember (id: $id, page: $page, limit: $limit) {
      edges {
        node {
          avatar
        }
      }
    }
  }
`;

export {
  GET_CHANNEL_BY_SLUG,
  GET_MEMBER_CHANNEL_BY_ID
}
