import React, {useState, useEffect} from 'react';
import clsx from 'clsx';
import {makeStyles} from '@material-ui/core/styles'
import { Card, CardContent, Grid, Typography, CardHeader, Avatar, TextField, Button, Icon, IconButton} from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';
import SendIcon from '@material-ui/icons/Send';
import moment from "moment"
import 'moment/locale/es'  // without this line it didn't work
moment.locale('vi')

const useStyles = makeStyles(theme => ({
  root: {},
  subheader: {
    flexWrap: 'wrap',
    display: 'flex',
    alignItems: 'center'
  },
  stars: {
    display: 'flex',
    alignItems: 'center',
    marginRight: theme.spacing(1)
  },
  content: {
    padding: 0,
    '&:last-child': {
      paddingBottom: 0
    }
  },
  message: {
    padding: theme.spacing(2, 3)
  },
  details: {
    padding: theme.spacing(1, 3)
  }
}));

interface OverallReviewsProps {
  className?:string,
}

let reviews = [
  {
    id: "141545616541654",
    reviewer: {
      avatar: "https://zicxa.com/hinh-anh/wp-content/uploads/2019/07/T%E1%BB%95ng-h%E1%BB%A3p-h%C3%ACnh-%E1%BA%A3nh-g%C3%A1i-xinh-d%E1%BB%85-th%C6%B0%C6%A1ng-cute-nh%E1%BA%A5t-6.jpg",
      name: "Nguyễn Hồng Thúy"
    },
    rating: 3,
    created_at: "1 3 2020",
    message: "Hùng thật sự rất tuyệt vời mỗi khi gặp khách hàng",
  }, 
  {
    id: "215474854156478",
    reviewer: {
      avatar: "https://thuthuatnhanh.com/wp-content/uploads/2019/07/anh-girl-xinh-facebook-tuyet-dep-387x580.jpg",
      name: "Mai Trâm"
    },
    rating: 4,
    created_at: "1 1 2020",
    message: "Tư vấn rất chuyên nghiệp nhiệt tình, khá thân thiện với khách hàng. Tôi đánh giá rất cao chuyên môn của bạn",
  }
]



const Project: React.FunctionComponent<OverallReviewsProps> = (props: OverallReviewsProps) => {
  const [value, setValue] = React.useState<number | null>(3);
  const [rating, setRating] = React.useState<number | null>(0)
  let couter: number = 0
  reviews.map((item: any, index: number) => {
    couter = Number(item.rating) + couter
  })

  React.useEffect(() => {
    
  })

  let ratingGeneral = couter/reviews.length
  const { className, ...rest } = props;
  const classes = useStyles();
  return (
    <div  className={clsx(classes.root, className)}>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card className={clsx(classes.root, className)}>
            <CardContent>
              <Grid alignItems="center" container spacing={3}>
                <Grid item>
                  <Typography variant="h5">Đánh giá tổng quan</Typography>
                </Grid>
                <Grid className={classes.stars} item>
                  <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                    <Rating name="simple-controlled" value={ratingGeneral} />
                  </Box>
                </Grid>
                <Grid item>
                  <Typography color="textSecondary" variant="body2" >
                    {reviews.length} lượt đánh giá
                  </Typography>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {reviews.map((item: any, index: number) => {
        return (
          <Grid container spacing={4} key={index}>
            <Grid item xs={12}>
              <Card>
                <CardHeader 
                  avatar={
                    <Avatar alt="Reviewer" src={item.reviewer.avatar} >
                      {item.reviewer.name}
                    </Avatar>
                  }
                  disableTypography
                  subheader={
                    <div className={classes.subheader}>
                      <div className={classes.stars}>
                        <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                          <Rating
                            disabled={true}
                            name={`simple-controlled${index}`}
                            value={value}
                            onChange={(event, newValue) => {
                              setValue(newValue);
                            }}
                          />
                        </Box>
                      </div>
                      <Typography variant="body2">
                        Đánh giá bởi{' '} <b>{item.reviewer.name}</b> | {moment(item.created_at).fromNow()}
                      </Typography>
                    </div>
                  }
                />
                <CardContent>
                  <div className={classes.message}>
                    <Typography variant="subtitle2">{item.message}</Typography>
                  </div>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        )  
      })}
      
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Box component="fieldset" mb={3} borderColor="transparent" style={{ marginBottom: "-5px" }}>
                <Rating
                  name="simple-controlled_"
                  value={rating}
                  onChange={(event, newValue) => { setRating(newValue) }}
                />
              </Box>
              <TextField
                multiline={true}
                fullWidth={true}
                rowsMax={10}
                margin="dense"
                name="reviews"
                placeholder="Đánh giá ..."
                variant="outlined"
              />
              <IconButton color="primary"><SendIcon/>Gửi</IconButton>
              <Button>
                Gửi
                <svg viewBox="64 64 896 896" width="1em" height="1em" fill="currentColor" >
                  <path d="M931.4 498.9L94.9 79.5c-3.4-1.7-7.3-2.1-11-1.2a15.99 15.99 0 00-11.7 19.3l86.2 352.2c1.3 5.3 5.2 9.6 10.4 11.3l147.7 50.7-147.6 50.7c-5.2 1.8-9.1 6-10.3 11.3L72.2 926.5c-.9 3.7-.5 7.6 1.2 10.9 3.9 7.9 13.5 11.1 21.5 7.2l836.5-417c3.1-1.5 5.6-4.1 7.2-7.1 3.9-8 .7-17.6-7.2-21.6zM170.8 826.3l50.3-205.6 295.2-101.3c2.3-.8 4.2-2.6 5-5 1.4-4.2-.8-8.7-5-10.2L221.1 403 171 198.2l628 314.9-628.2 313.2z"></path></svg>
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
};


export default Project;
