import React, {useEffect} from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/Project/ProjectMain";
import DetailInvestor from "../../components/Investor/DetailInvestor"
import {apolloClient} from "../../utils";
import {GET_PROJECT_BY_SLUG} from "../../graphql/project/queries";
import {GET_INVESTOR_BY_ID} from "../../graphql/investor/queries"
import StringProcess from "../../modules/stringProcess";
import "../../assets/scss/style.scss";
import {localStorageCompareKey, mainURL} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import {COUNT_VIEW_PROJECT} from "../../graphql/project/mutation";

interface ProjectItemProp {
  id: string,
  investor: string,
  price: string,
  name: string,
  land_area: string,
  image: ImageProp[],
  video: string,
  financial_support: string,
  position_content: string,
  infrastructure_content: string,
  seo_title: string|null,
  seo_description: string|null,
  address: { 
    text: string,
    ward: string, 
    district: string, 
    city: string 
  },
  block: {
    name: string,
    image: ImageProp,
  }[],
  cover_image: string,
  intro_content: string
  created_at: string
  updated_at: string
  view_count: number
  google_map_latitude: string
  google_map_longitude: string
}
interface ImageProp { 
  alt_text: string 
  description: string
  title: string
  src: string
}
interface ProjectDetails {
  url: { query: { slug: string }},
  projectDetail: { data: { project: ProjectItemProp } },
  slug: string
}
interface QueryProp { 
  query: { slug: string } 
}
interface CompareDataProps {
  avatar: string,
  title: string|null,
  id: string,
  type: string
}
interface Investor {
  id: string
  name: string
  description: string
  slug: string
  email: string
  website: string
  image: string
  address: {
   text: string
  }
  phone: string
  mobile: string
}
interface ImageProp {
  alt_text: string
  description: string
  title: string
  src: string
}
 
// @ts-ignore
const ProjectDetailPage: NextPage<ProjectDetails> = (props: ProjectDetails) => {
  let title = 'Xem chi tiết dự án';
  let square = (<>m<sup>2</sup></>);
  let imageAvatar = 'https://static.nhadatmoi.net/default/home_default.jpg';
  
  const freshText = (input: string) => {
    input = StringProcess.stripProperties(input, []);
    input = StringProcess.stripTags(input, '');
    input = input.replace(/&nbsp;/g, ' ');
    return input;
  };
  const convertDescription = (input: string) => {
    return input.replace(/&#(\d+);/g, function(match, dec) {
      return String.fromCharCode(dec);
    });
  };

  let metaTitle = 'metaTitle';
  let metaDescription = 'metaDescription';
  
  let initStateInvestor = {
    id: "",
    name: "",
    description: "",
    slug: "",
    email: "",
    website: "",
    image: "",
    address: {
      text: "",
    },
    phone: "",
    mobile: ""
  }

  //Count Ad Sell Lease
  const [investor, setInvestor] = React.useState<Investor>(initStateInvestor);
  
  useEffect(() => {
    let projectId = "props.projectDetail.data.project.id"
  }, []);

  const [dataCompare, setDataCompare] = React.useState({});
  let updateCompareData = (data: CompareDataProps) => {
    let dataToCompare = [];
    let isAddToLocal = true;
    if (localStorage.getItem(localStorageCompareKey) && !Validator.isBlank(localStorage.getItem(localStorageCompareKey))) {
      dataToCompare = JSON.parse(localStorage.getItem(localStorageCompareKey) as string);
      for (let index in dataToCompare) {
        if (dataToCompare[index].id === data.id) {
          isAddToLocal = false;
        }
        if (dataToCompare[index].type !== data.type) {
          localStorage.removeItem(localStorageCompareKey);
          dataToCompare = [];
          break;
        }
      }
    }
    if (isAddToLocal) {
      dataToCompare.push(data);
      // @ts-ignore
      setDataCompare(dataToCompare);
      localStorage.setItem(localStorageCompareKey, JSON.stringify(dataToCompare));
    }
  };
  return (
    <Layout
      title={"metaTitle"}
      description={"metaDescription"}
      {...props}
      image={"https://www.w3schools.com/w3css/img_lights.jpg"}
      canonical={ mainURL + '/nguoi-dung/' + "ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485"}
      path={'nguoi-dung'}
      dataCompare={{}}
    >
      <h1>Dai ca</h1>
      {/* <DetailInvestor investor={investor} slug={"ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485"} /> */}
    </Layout>
  );
};

// @ts-ignore
ProjectDetailPage.getInitialProps = async function(props: QueryProp) {
  let projectSlug = props.query.slug;
  // let projectDetail = await apolloClient.query({query: GET_PROJECT_BY_SLUG, variables: {
  //     "slug": projectSlug
  //   }}).then(response => {
  //   return response;
  // });
  return {
    projectDetail: {},
    slug: projectSlug,
  };
};

export default ProjectDetailPage;
