import React from "react";
import { Avatar, Typography, Button, Grid } from "@material-ui/core";
import StyledCheckbox from './StyledCheckbox'
import { headerStyles } from './Constant'
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import { FOLLOW_CHANNEL, UNFOLLOW_CHANNEL } from "../../graphql/channel/mutation"
import { apolloClient } from "../../utils";
import _ from "lodash"

interface HearderProps {
  userLogin: string
  slug: string
  channel: ChannelProp
}

interface ChannelProp {
  id: string
  name: string
  slug: string
  avatar: string
  cover: string
  description: string
  nation: string
  language: string
  telephone: string
  address: string
  facebook: string
  youtube: string
  website: string
  official_status: number
  follow_value: number
  followers: {
    user_id: string
  }[]
  created_at: string
}

const Header: React.FunctionComponent<HearderProps> = (props: HearderProps) => {
  const classes = headerStyles();
  const avatar = "https://sf.co.ua/14/07/wallpaper-117233.jpg"; // "https://news.mogi.vn/wp-content/uploads/2018/08/anh-nha-dep-13.jpg"
  let follow_value = _.get(props, "channel.follow_value", "");
  let [followValue, setFollowValue] = React.useState(0);
  
  const addFollow = async ()=> { 
    let follow = await apolloClient.mutate({ mutation: FOLLOW_CHANNEL, variables: {
      "input": {
        "id": _.get(props, "channel.id", "")
      }
    }})
    .then((response: any) => {
      setFollowValue(follow_value + 1 )
    })
  };

  const unfollow = async ()=> { 
    let follow = await apolloClient.mutate({ mutation: UNFOLLOW_CHANNEL, variables: {
      "input": {
        "id": _.get(props, "channel.id", "")
      }
    }})
    .then((response: any) => {
      setFollowValue(follow_value - 1)
    })
  };

  let followers = _.get(props, "channel.followers", [])
  console.log("followers", followers)
  const follow = followValue || follow_value
  let chechFollow = false 
  if(Array.isArray(followers)){
    followers.map((item: any, index) => {
      if(props.userLogin === item.user_id) {
        chechFollow = true
      }
    })
  }
  return (
    <div>
      <div className={classes.cover}></div>
      <div className={classes.container}>
        <Avatar
          alt="nhadatmoi.net"
          className={classes.avatar}
          src={avatar}
        />   
        <Grid container spacing={0} alignContent="center">
          <Grid item xs={8} sm={6}>
            <div className={classes.titleFollow}>
              <Typography component="h2" className={classes.title}> 
                {_.get(props, "channel.name", "")} { _.get(props, "channel.official_status", "") ? <CheckCircleIcon className={classes.ratingColor}/> : ""} 
              </Typography>
              <Typography component="h3" variant="body2" className={classes.follow}>
                {follow > 0 ? follow : 0} lượt theo dõi
              </Typography>
            </div>
          </Grid>
          <Grid item xs={4} sm={6}>
            <div className={classes.followStyle}>
              <Button color="primary" onClick={() => addFollow()} fullWidth variant="outlined">Theo dõi</Button>
            </div>
            <div className={classes.followStyle}>
              <Button color="primary" onClick={() => unfollow()} fullWidth variant="contained">Bỏ theo dõi</Button>
            </div>
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Header;
