import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import { Avatar, Typography, Hidden, colors, Button, Icon, IconButton, Grid } from "@material-ui/core";
import StyledCheckbox from './StyledCheckbox'

const useStyles = makeStyles((theme) => ({
  root: {},
  cover: {
    position: "relative",
    height: 350,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundImage: `url(https://i.pinimg.com/originals/a8/45/76/a84576a04c1874304735604d9f47d5a4.jpg)`,
    "&:before": {
      position: "absolute",
      content: '" "',
      top: 0,
      left: 0,
      height: "100%",
      width: "100%",
      backgroundImage: "linear-gradient(-180deg, rgba(0,0,0,0.00) 58%, rgba(0,0,0,0.32) 100%)",
    },
    "&:hover": {
      "& $changeButton": {
        visibility: "visible",
      },
    },
  },
  changeButton: {
    visibility: "hidden",
    position: "absolute",
    bottom: theme.spacing(3),
    right: theme.spacing(3),
    backgroundColor: colors.blueGrey[900],
    color: theme.palette.divider,
    [theme.breakpoints.down("md")]: {
      top: theme.spacing(3),
      bottom: "auto",
    },
    "&:hover": {
      backgroundColor: colors.blueGrey[900],
    },
  },
  addPhotoIcon: {
    marginRight: theme.spacing(1),
  },
  container: {
    width: theme.breakpoints.values.lg,
    maxWidth: "100%",
    padding: theme.spacing(1, 3),
    margin: "0 auto",
    position: "relative",
    display: "flex",
    flexWrap: "wrap",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  checkbox: { 
    float: "right", 
    display: "inline"
  },
  avatar: {
    border: `2px solid ${theme.palette.divider}`,
    height: 120,
    width: 120,
    top: -60,
    left: theme.spacing(3),
    position: "absolute",
    [theme.breakpoints.down("sm")]: {
      height: 80,
      width: 80,
    },
  },
  details: {
    marginLeft: 136,
  },
  actions: {
    marginLeft: "auto",
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(1),
    },
    "& > * + *": {
      marginLeft: theme.spacing(1),
    },
  },
  pendingButton: {
    color: theme.palette.divider,
    backgroundColor: colors.red[600],
    "&:hover": {
      backgroundColor: colors.red[900],
    },
  },
  personAddIcon: {
    marginRight: theme.spacing(1),
  },
  mailIcon: {
    marginRight: theme.spacing(1),
  },
}));

interface HeaderProps {
  className?: string;
  infoUser: UserProp;
}

interface UserProp {
  address: string;
  avatar: string;
  birthday: string;
  email: string;
  facebook: string;
  first_name: string;
  gender: string;
  google_id: string;
  id: string;
  intro: string;
  last_name: string;
  phone: string;
  mobile: string;
  skype: string;
  username: string;
  viber: string;
  website: string;
  zalo: string;
  created_at: string;
  updated_at: string;
}

const Header: React.FunctionComponent<HeaderProps> = (props: HeaderProps) => {
  const { className } = props;
  const classes = useStyles();
  let avatarUser = String(props.infoUser.avatar);
  return (
    <div>
      <div className={classes.cover}></div>
      <div className={classes.container}>
        <Avatar
          alt="nhadatmoi.net"
          className={classes.avatar}
          src={"https://news.mogi.vn/wp-content/uploads/2018/08/anh-nha-dep-13.jpg"}
        />
        <div className={classes.details}>
          <Typography component="h2" gutterBottom variant="h6"> Nhà Đất Mới </Typography>
          <StyledCheckbox
            defaultChecked={true}
            className={classes.checkbox}
          />
          <Typography component="h3" variant="body2" className={classes.follow}>
            23.456 lượt theo dõi
          </Typography>
          {/* <Button color="primary" variant="outlined" style={{display: "inline", marginLeft: "100px", paddingLeft: "50px", paddingRight: "50px" }}>
            Theo dõi
          </Button> */}
        </div>
        {/* <Hidden smDown> */}
        <div className={classes.actions}></div>
        <div>
          <Button
            color="primary"
            variant="outlined"
            style={{
              display: "inline",
              marginLeft: "50px",
              marginTop: "5px",
              paddingLeft: "50px",
              paddingRight: "50px",
            }}
          >
            Theo dõi
          </Button>
        </div>
        {/* </Hidden> */}
      </div>
    </div>
  );
};

export default Header;
