import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import {
  Avatar,
  Typography,
  Hidden,
  colors,
  Button,
  Icon, IconButton,
  Grid
} from '@material-ui/core';


const useStyles = makeStyles(theme => ({
  root: {},
  cover: {
    position: 'relative',
    height: 300,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    '&:before': {
      position: 'absolute',
      content: '" "',
      top: 0,
      left: 0,
      height: '100%',
      width: '100%',
      backgroundImage:
        'linear-gradient(-180deg, rgba(0,0,0,0.00) 58%, rgba(0,0,0,0.32) 100%)'
    },
    '&:hover': {
      '& $changeButton': {
        visibility: 'visible'
      }
    }
  },
  changeButton: {
    visibility: 'hidden',
    position: 'absolute',
    bottom: theme.spacing(3),
    right: theme.spacing(3),
    backgroundColor: colors.blueGrey[900],
    color: theme.palette.divider,
    [theme.breakpoints.down('md')]: {
      top: theme.spacing(3),
      bottom: 'auto'
    },
    '&:hover': {
      backgroundColor: colors.blueGrey[900]
    }
  },
  addPhotoIcon: {
    marginRight: theme.spacing(1)
  },
  container: {
    width: theme.breakpoints.values.lg,
    maxWidth: '100%',
    padding: theme.spacing(2, 3),
    margin: '0 auto',
    position: 'relative',
    display: 'flex',
    flexWrap: 'wrap',
    [theme.breakpoints.down('sm')]: {
      flexDirection: 'column'
    }
  },
  avatar: {
    border: `2px solid ${theme.palette.divider}`,
    height: 120,
    width: 120,
    top: -60,
    left: theme.spacing(3),
    position: 'absolute',
    [theme.breakpoints.down('sm')]: {
      height: 80,
      width: 80,
    }
  },
  details: {
    marginLeft: 136
  },
  actions: {
    marginLeft: 'auto',
    [theme.breakpoints.down('sm')]: {
      marginTop: theme.spacing(1)
    },
    '& > * + *': {
      marginLeft: theme.spacing(1)
    }
  },
  pendingButton: {
    color: theme.palette.divider,
    backgroundColor: colors.red[600],
    '&:hover': {
      backgroundColor: colors.red[900]
    }
  },
  personAddIcon: {
    marginRight: theme.spacing(1)
  },
  mailIcon: {
    marginRight: theme.spacing(1)
  }
}));

interface HeaderProps {
  className?:string,
  infoUser: UserProp
}

interface UserProp {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  id: string
  intro: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

const Header:React.FunctionComponent<HeaderProps> = (props:HeaderProps) => {
  const { className } = props
  const classes = useStyles()
  let avatarUser = String(props.infoUser.avatar)
  return (
    <div>
      <div className={classes.cover} style={{ backgroundImage: `url(https://hanoimoi.com.vn/Uploads/Album/20190309/282a5c47-7fe7-4325-a65d-1c3be7bd8ea7.jpg)`}}>
      </div>
      <div className={classes.container}>
        <Avatar alt="nhadatmoi.net" className={classes.avatar} src={avatarUser} />
        <div className={classes.details}>
          <Typography component="h2" gutterBottom variant="h6">
            Nhà Đất Mới
          </Typography>
          <Typography component="h3" variant="body2" style={{ display: "inline", color: "#aaa" }} >
            23.456 lượt theo dõi
          </Typography>
          {/* <Button color="primary" variant="outlined" style={{display: "inline", marginLeft: "100px", paddingLeft: "50px", paddingRight: "50px" }}>
            Theo dõi
          </Button> */}
        </div>
        {/* <Hidden smDown> */}
          <div className={classes.actions}>
            <a rel="nofollow" title="Facebook" target="_blank" href="https://www.facebook.com/profile.php?id=100012135152590"> 
              <img width="44px" height="44px" alt="Facebook" src="https://zozo.vn/public/theme/circle/images/icons/fb-mau.svg"/> 
            </a>
            <a title="Trò chuyện thông qua Facebook Messenger" target="_blank" href="https://www.facebook.com/messages/t/100008304697272"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/fbmessenger.svg" alt="btn-facebook"/>
            </a>
            <a title="Chat với chúng tôi qua Zalo" target="_blank" href="https://zalo.me/0377535717"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/zalo.svg" alt="btn-zalo"/>
            </a>
            <a title="Gọi ngay" href="tel:0936162822"> 
              <img src="https://zozo.vn/public/theme/circle/images/widgets/call.svg" alt="btn-callnow"/>
            </a>
          </div>
          <div>
            <Button color="primary" variant="outlined" 
              style={{
                display: "inline", 
                marginLeft: "50px", 
                marginTop: "5px", 
                paddingLeft: "50px", 
                paddingRight: "50px" 
              }}>Theo dõi
            </Button>
          </div>
        {/* </Hidden> */}
      </div>
    </div>
  )
}

export default Header;
