import React from 'react';
import { Button, Typography, Grid, Icon } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import SearchIcon from '@material-ui/icons/Search';
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    paper: {
      marginRight: theme.spacing(2),
    },
  }),
);

const dataProjectKey = [
  {
    title: "Vinhomes Central Park",
    value:  100,
  },
  {
    title: "Vinhomes Grand Park",
    value:  101
  },
  {
    title: "Vinhomes Smart City",
    value:  102
  },
  {
    title: "Nhà Đất Hải Phòng",
    value:  108
  },
  {
    title: "Nhà Đất Hà Nội",
    value:  109
  }
]


export default function MenuListComposition() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [valueSearch, setValueSearch] = React.useState("");
  const anchorRef = React.useRef(null);

  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen);
  };

  const handleClose = (event: any, index: number) => {
    let titleSearch = dataProjectKey[index].title || ""
    setValueSearch(titleSearch)
    // if (anchorRef.current && anchorRef.current.contains(event.target)) {
    //   return;
    // }
    setOpen(false);
  };

  function handleListKeyDown(event: any) {
    if (event.key === 'Tab') {
      event.preventDefault();
      setOpen(false);
    }
  }

  const prevOpen = React.useRef(open);
  React.useEffect(() => {
    if (prevOpen.current === true && open === false) {
      // anchorRef.current.focus();
    }
    prevOpen.current = open;
  }, [open]);

  const viewSearch = dataProjectKey.map((item, index) => {
    return (
      <MenuItem key={index} onClick={(event: any) => handleClose(event, index)}>{item.title}</MenuItem>
    )
  })


  return (
    <div className={classes.root}>
      <Grid container spacing={1} justify="flex-start" alignItems="center">
        <Grid item xs={4}>
          <TextField
            fullWidth={true}
            id="outlined-basic" 
            variant="outlined"
            value={valueSearch}
            autoComplete="off"
            ref={anchorRef}
            margin="dense"
            onClick={handleToggle}
          />
        </Grid>
        <Grid item xs={6}>
          <Button><SearchIcon />Tìm kiếm</Button>
        </Grid>
        <Grid item xs={2}></Grid>
        <Grid item xs={12}>
          <Popper open={open} anchorEl={anchorRef.current} role={undefined} transition disablePortal>
              {({ TransitionProps, placement }) => (
                <Grow
                  {...TransitionProps}
                  style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
                >
                  <Paper>
                    <ClickAwayListener onClickAway={() => {}}> 
                    {/* // onClick={(event: any) => handleClose(event, 100)}
                    // onClick={(event: any) => handleClose(event, index) */}
                      <MenuList autoFocusItem={open} id="menu-list-grow" onKeyDown={handleListKeyDown}>
                        <MenuItem>
                          <Typography variant="body" style={{ fontSize: "Roboto, sans-serif",  }}>
                            TỪ KHÓA HÓT
                          </Typography>
                        </MenuItem>
                          {viewSearch}
                        <MenuItem>
                          <Typography style={{ fontSize: "Roboto, sans-serif", marginRight: "120px" }} variant="body">
                            LỊCH SỬ TÌM KIẾM
                          </Typography>
                          <Typography style={{ color: "red" }}>Xóa </Typography>
                          </MenuItem>
                        {/* <MenuItem onClick={handleClose}>Khu đô thị Xala</MenuItem>
                        <MenuItem onClick={handleClose}>Khu đô thị Timecity</MenuItem>
                        <MenuItem onClick={handleClose}>Khu đô thị dương nội</MenuItem>
                        <MenuItem onClick={handleClose}>Nhà đất mới </MenuItem> */}
                      </MenuList>
                    </ClickAwayListener>
                  </Paper>
                </Grow>
              )}
            </Popper>
        </Grid>
      </Grid>
    </div>
  );
}
