import React from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import {apolloClient} from "../../utils";
import { GET_CHANNEL_BY_SLUG } from "../../graphql/channel/queries";
import "../../assets/scss/style.scss";
import { mainURL } from "../../constants";
import Channel from "../../components/Channel/Index"
import _ from "lodash"
import { Typography, CardHeader, Avatar, Grid } from '@material-ui/core';
import Rating from '@material-ui/lab/Rating';
import { ratings, ratingStyles } from "./Constant"
import GradeIcon from '@material-ui/icons/Grade';


// @ts-ignore
const UserPage: NextPage<IndexProps> = (props: IndexProps) => {
  const classes = ratingStyles();
  const renderAverageRating = () => {
    const total = 3
    const arrTotalRating = [
      { width: `60%` },
      { width: `10%` },
      { width: `10%` },
      { width: `5%` },
      { width: `5%` },
    ]
    return (
      <div className={classes.averageRating}>
        <div className={classes.averageRatingTotal}>
          <p className={classes.averageRatingLevel}>3.0</p>
          <Grid container alignItems="center" justify="center" spacing={0} style={{ marginTop: "-12px" }}>
            <Grid item xs={8}>
              <Rating disabled name="simple-controlled" value={3} style={{ color: "#045EA8" }}/>
            </Grid>
            <Grid item xs={4}><Typography variant="h5">{total}</Typography></Grid>
          </Grid>
        </div>
        <div className={classes.averageRatingDescribe}>
          {arrTotalRating.map((item: any, index: number) => {
            return (
              <div>
                <div style={{ width: "50px", height: 20, float: "left", marginTop: '-4px' }}>
                  {5 - index}
                  <GradeIcon style={{ width: "18px", margin: '0 0 -6px 5px', color: 'gray'}}/>
                </div>
                <div className={classes.averageRatingDescribeFive} key={index}>
                  <div className={classes.averageRatingDescribeFiveLevel} style= {{ width: item.width }}></div>
                </div>
              </div>
            )
          })}
        </div>
        <div style={{ clear: "both" }}></div>
      </div>
    )
  }

  return (
    <Layout
      title={"metaTitle"}
      description={""}
      {...props}
      image={"imgBanner"}
      canonical={ mainURL + '/channel/' + props.slug}
      path={'channel'}
    >
      <div style={{ margin: "0 auto", maxWidth: "1600px" }}>
      <Grid container spacing={0} >
        <Grid item xs={9}>
          {renderAverageRating()}
        </Grid>
        <Grid item xs={3}>
          <h1> 333333333333 33333333333 33333333333 33333333333 3333333333333333333 333333333333333333</h1>
        </Grid>
      </Grid>
      </div>
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  
  return {
  };
};


export default UserPage;
