import React, { FunctionComponent } from 'react';
import {makeStyles} from '@material-ui/core/styles'
import Info from './Tabs/Info'
import Home from './Tabs/Home'
import Work from './Tabs/Work'
import Video from './Tabs/Video'
import Introduce from './Tabs/Info'
import Comments from "./Tabs/Comments"
import AdSellLeases from "./Tabs/AdSellLeases"
import Header from "./Header"
import Paper from '@material-ui/core/Paper'
import StyledCheckbox from "./StyledCheckbox"
import {
  Tabs, Tab, Divider, colors, Grid,
  Button, Icon, IconButton, Typography,
  Card, CardContent, CardActions,
  Avatar, Checkbox
} from '@material-ui/core';
import Page from "./Page";
 
const useStyles = makeStyles(theme => ({
  root: {},
  rootTabs: {
    flexGrow: 1,
  },
  large: {
    width: theme.spacing(7),
    height: theme.spacing(7),
  },
  inner: {
    padding: theme.spacing(3)
  },
  divider: {
    backgroundColor: colors.grey[300]
  },
  content: {
    marginTop: theme.spacing(3)
  }
}));

interface IndexProps {
  comments: CommentsProp[],
  infoUser: UserProps,
  ratings: RatingsProp[],
}

interface CommentsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  },
}

interface UserProps {
  address: string
  avatar: string
  birthday: string
  email: string
  facebook: string
  first_name: string
  gender: string
  google_id: string
  intro: string
  id: string
  last_name: string
  phone: string
  mobile: string
  skype: string
  username: string
  viber: string
  website: string
  zalo: string
  created_at: string
  updated_at: string
}

interface RatingsProp {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    rate: number,
    like_number: string,
    created_at: string,
  },
}

const Index: FunctionComponent<IndexProps> = (props: IndexProps) => {
  const classes = useStyles();
  const [itemTab, setItemTab] = React.useState("home")
  const [count, setCount] = React.useState<number>(0)
  const tabs = [
    { value: 'home', label: 'Tổng quan'},
    { value: 'info', label: 'Thông tin'},
    // { value: 'ad', label: 'Tin rao'},
    { value: 'work', label: "Hoạt động"},
    { value: "video", label: "Video"},
    { value: "introduce", label: "Giới thiệu"},
  ]

  const handleTabsChange = (event: any, value: any) => {
    setItemTab(value)
  }

  const relatedChannel = [
    { title: "Mua bán bất động sản",  follow: true, numberfollow: "23.365", image: "https://image.plo.vn/w653/Uploaded/2020/ongqbatfnacy2/2016_06_09/download_isam.jpg" },
    { title: "Thiết kế thi công nhà ở", follow: true, numberfollow: "45.365", image: "https://media.ngoisao.vn/news/2018/08/14/phong-thuy10-ngoisao.vn-w580-h387.jpg" },
