    { title: "Khu thương mại cao ốc",  follow: true, numberfollow: "45.365", image: "https://land24h.net/wp-content/uploads/2018/05/chung-cu-green-stars.jpg" },
    { title: "Mua bán bất động sản", follow: false, numberfollow: "23.365", image: "https://image.plo.vn/w653/Uploaded/2020/ongqbatfnacy2/2016_06_09/download_isam.jpg" },
    { title: "Thiết kế thi công nhà ở", follow: true, numberfollow: "45.365", image: "https://media.ngoisao.vn/news/2018/08/14/phong-thuy10-ngoisao.vn-w580-h387.jpg" },
    { title: "Thuê văn phòng giá rẻ",  follow: false, numberfollow: "28.365", image: "https://cafefcdn.com/thumb_w/650/2017/du-an-vincity-tay-mo-dai-mo-1489197061940.jpg" },
    { title: "Cho thuê bất động sản",  follow: false, numberfollow: "108.365", image: "https://phongthuytamnguyen.com/uploads/images/2018_06_16_fea1a1c344.jpeg" },
    { title: "Mua bán bất động sản", follow: true, numberfollow: "23.365", image: "https://image.plo.vn/w653/Uploaded/2020/ongqbatfnacy2/2016_06_09/download_isam.jpg" },
