                if( index < count*5 + 10) {
                  return (
                    <div style={{ width: "300px", height: "80px", border: "2px solid #D9EDFE", marginBottom: "5px"  }}>
                      <Grid key={index} container alignItems="center" justify="center" spacing={4}>
                        <Grid item xs={2}>
                          <Avatar src={item.image} className={classes.large} alt="" />
                        </Grid>
                        <Grid item xs={10}>
                          <Typography style={{ display: "inline" }}>
                            {item.title}
                          </Typography>
                          { item.follow && <StyledCheckbox defaultChecked={item.follow}/> }
                          <Typography style={{ color: "#aaa", fontSize: "13px"}}>
                            {item.numberfollow} Lượt theo dõi
                          </Typography>
                        </Grid>
                      </Grid>
                    </div>
                  )
                } else {
                  return ""
                }
              })}
              <Grid container spacing={2}>
                <Button fullWidth onClick={() => setCount(count + 1)} >Xem thêm</Button>
              </Grid>
            </Grid>
          </Grid>
        </CardContent>
      </Page>
  )
}

export default Index;
