import React, {useEffect} from 'react';
import {NextPage} from "next";
import Layout from '../../components/Layout/Layout';
import BannerSlider from "../../components/PageDetail/BannerSlider";
import Main from "../../components/Project/ProjectMain";
import DetailInvestor from "../../components/Investor/DetailInvestor"
import {apolloClient} from "../../utils";
import {GET_AD_SELL_LEASE_COMMENTS} from "../../graphql/comments/queries";
import {GET_INVESTOR_BY_ID} from "../../graphql/investor/queries"
import StringProcess from "../../modules/stringProcess";
import "../../assets/scss/style.scss";
import {localStorageCompareKey, mainURL} from "../../constants";
import urlProcess from "../../modules/urlProcess";
import Validator from "../../modules/validator";
import {COUNT_VIEW_PROJECT} from "../../graphql/project/mutation";

 
interface ProjectDetails {
  url: { query: { slug: string }},
  coments: listCommentProps,
  slug: string,
}

interface QueryProp { 
  query: { slug: string } 
}

interface listCommentProps {
  node: {
    id: string,
    ad_sell_lease_id: string,
    content: string,
    user_id: string,
    media: string,
    user: {
      first_name: string,
      last_name: string,
      avatar: string,
    }
    like_number: string,
    created_at: string,
  }[],
}

// @ts-ignore
const UserPage: NextPage<ProjectDetails> = (props: ProjectDetails) => {
  console.log("coments", props.coments)

  return (
    <Layout
      title={"metaTitle"}
      description={"metaDescription"}
      {...props}
      image={"https://www.w3schools.com/w3css/img_lights.jpg"}
      canonical={ mainURL + '/nguoi-dung/' + "ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485"}
      path={'nguoi-dung'}
      dataCompare={{}}
    >
      <h1>Dai ca</h1>
      {/* <DetailInvestor investor={investor} slug={"ra-hang-dot-cuoi-toa-nha-discovery-complex-so-302-cau-giay-gia-chi-tu-31trm2-1585625310485"} /> */}
    </Layout>
  );
};

// @ts-ignore
UserPage.getInitialProps = async function(props: QueryProp) {
  let adSlug = props.query.slug;
  let filterObject =  {
    slug: adSlug,
  };
  let dataFilter = JSON.stringify(filterObject);
  let listComment = await apolloClient.query({query: GET_AD_SELL_LEASE_COMMENTS, variables: {
      "page": 1,
      "limit": 100,
      "filter": dataFilter
    }}).then(response => {
    return response;
  });
  console.log("listComment", listComment.data.adSellLeaseComments.edges)
  return {
    listComment: listComment.data.adSellLeaseComments.edges,
    slug: adSlug,
  };
};

export default UserPage;
